	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			$('#gb-footer-globals').appendTo("#gb-footer-legal");

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
			$('#gb-footer-globals').appendTo("#gb-footer");
			$('#rs-menu-btn span').addClass("icon-font icon-menu-handle");

		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			$('#gb-footer-globals').appendTo("#gb-footer");
			$('#rs-menu-btn span').addClass("icon-font icon-menu-handle");

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			multimediaRotator();
			$('#rs-menu-btn span').addClass("icon-font icon-menu-handle");

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			$('#rs-menu-btn span').addClass("icon-font icon-menu-handle");
		}

		var getHeight = $(window).height();
		var additionalH = 0;
		if(announcement) {
			additionalH = $('#hp-emerg-container').outerHeight();
		}else{
			additionalH = 4;
		}
		$('#hp-content-outer').css('margin-top', getHeight - $('#sticky-header').height() - additionalH);
		$('#hp-slideshow .rs-gallery-desc, #hp-social-globals').css('top', $('#gb-textured-bg-container').height() + ($('#hp-slideshow').height() * 0.25));

			//keep checking if we need to stop the sticky header
		stickyHeader(1024);
		customGlobalScrollListner(480);

		/* Added by Jeremy - Case #00169913 */
		// Cause Homepage upcoming event li s to match height. 
		if( $('.hp').length > 0 ){
			var maxLiHeight = 0;
			$('#hp-bottom-app .upcomingevents .ui-articles > li').css('min-height', 0);
			$('#hp-bottom-app .upcomingevents .ui-articles > li').each(function(k, v){
				if( $(v).outerHeight() > maxLiHeight ) maxLiHeight = $(v).outerHeight();
			});
			$('#hp-bottom-app .upcomingevents .ui-articles > li').css('min-height', maxLiHeight);
		}
	}

