	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}



	function customDropdowns(){
		//Custom Dropdown Functionality
		//$('.custom-dropdown').click(function(e){
        $(document).on("click keydown", ".custom-dropdown", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
 	//e.preventDefault();
                if( $(this).hasClass("openMenu") ){
                    $(this).removeClass("openMenu");
                    $(this).children('p').children('span').removeClass('icon-minus').addClass('icon-plus');
                }else{
                    $('.custom-dropdown.openMenu').removeClass('openMenu').children('p').children('span').removeClass('icon-minus').addClass('icon-plus');
                    $(this).addClass("openMenu");
                    $(this).children('p').children('span').removeClass('icon-plus').addClass('icon-minus');
                }
            }
		})
		.hover(function(e){},function(e){
		    $(this).removeClass("openMenu");
		    $(this).children('p').children('span').removeClass('icon-minus').addClass('icon-plus');
		});


			//this is done so css can move that menu item to the right some so it doesnt display off of the page.
		$('#gb-custom-mystart > div.custom-dropdown:eq(0)').addClass("last-menu-item");
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the section thats id was passed to the function.
	** Return: N/A
	** Parameter(s): String (jQuery "#id") The id of the section to be scolled to.
	*/
	function smoothScroll( to, offset ){
		if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
		var target = $(to);

		if (target.length) {
			$('html,body').animate({
				scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
			}, 1000, function(){
				$("html, body").stop(true, false);
			});
			return false;
		}
	}

	function showHide(list, comp, target){
		var menuOptionString = "";

		$.each(list, function(k, v){
			if(v[0] === comp){
				$(k).remove();
			}else{
					//build out the menu option string
				menuOptionString += v[1][0] + "," + v[1][1] + "," + v[0] + "," + target + ";";
			}
		});

		return menuOptionString;
	}

	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	var scrollHookOn = 240;
	var scrollHookOff = 241;

	function stickyHeader(shutoff){
		var getWidth = $(window).width();

		if( getWidth >= shutoff ){
			var top = $(window).scrollTop();
				//check where the window is
			if(top >= scrollHookOn  && !$('#sticky-header').hasClass('on')){ //make it sticky
				$('#sticky-header-ghost').addClass('on').css('height', $('#sticky-header').outerHeight());
				$('#sticky-header').addClass('on').css('display','none').slideDown(400,'easeInOutExpo');

			}else if( top <= scrollHookOff  && $('#sticky-header').hasClass('on')){ //unstick it
				$('#sticky-header').removeClass('on');
				$('#sticky-header-ghost').removeClass('on');
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#sticky-header").outerHeight(true));
                }
            }
		}else if( $('#sticky-header').hasClass('on') ){
			$('#sticky-header').removeClass('on');
			$('#sticky-header-ghost').removeClass('on');
		}
	}

	// Grabbed from http://stackoverflow.com/questions/5560248/programmatically-lighten-or-darken-a-hex-color-or-rgb-and-blend-colors
	function blendColors(c0, c1, p) {
	    var f=parseInt(c0.slice(1),16),t=parseInt(c1.slice(1),16),R1=f>>16,G1=f>>8&0x00FF,B1=f&0x0000FF,R2=t>>16,G2=t>>8&0x00FF,B2=t&0x0000FF;
	    return "#"+(0x1000000+(Math.round((R2-R1)*p)+R1)*0x10000+(Math.round((G2-G1)*p)+G1)*0x100+(Math.round((B2-B1)*p)+B1)).toString(16).slice(1);
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};


