    function headlineMoreLinkCheck(){

    	$("#hp-content-row-one .ui-widget.app.headlines").each(function(){
        	if($(".more-link",this).length){
        	var moreLink = $(".more-link",this);

            $(".ui-widget-detail",this).append(moreLink);
            $(this).addClass("has-more-link");
        }
        });
    	/*if($("#hp-content-row-one .headlines .more-link").length){
