	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				//DOC replace view calendar text
                $("a.view-calendar-link").each(function() {
                    $(this).text("View Calendar");
                    $(this).appendTo($(this).parent().parent().parent().children(".ui-widget-header"));
                });
			break;
			case "768":
				//DOC replace view calendar text
                $("a.view-calendar-link").each(function() {
                    $(this).text("View Calendar");
                    $(this).appendTo($(this).parent().parent().parent().children(".ui-widget-header"));
                });
                var slideshowHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-images').height();
				$('#hp-slideshow .rs-gallery-container .rs-gallery-control').css('top', slideshowHeight - 68);
			break;
			case "640":
				appView();
                //DOC replace view calendar text
                $("a.view-calendar-link").each(function() {
                    $(this).text("View Calendar");
                    $(this).appendTo($(this).parent().parent().parent().children(".ui-widget-header"));
                });
                var slideshowHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-images').height();
				$('#hp-slideshow .rs-gallery-container .rs-gallery-control').css('top', slideshowHeight - 68);
			break;
			case "480":
				appView();
                //DOC replace view calendar text
                $("a.view-calendar-link").each(function() {
                    $(this).text("View Calendar");
                    $(this).appendTo($(this).parent().parent().parent().children(".ui-widget-header"));
                });
                var slideshowHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-images').height();
				$('#hp-slideshow .rs-gallery-container .rs-gallery-control').css('top', slideshowHeight - 68);
			break;
			case "320":
				appView();
                var slideshowHeight = $('#hp-slideshow .rs-gallery-container .rs-gallery-images').height();
				$('#hp-slideshow .rs-gallery-container .rs-gallery-control').css('top', slideshowHeight - 5);
			break;
		}
	}

