                            "descVideoLinkText"       : "Watch Video",
                            "bullets"                 : "no",
                            "bulletControls"          : "no",
                            "thumbnails"              : "no",
                            "thumbnailWidth"          : 122,
                            "thumbnailHeight"         : 53,
                            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                            "hoverPause"              : "yes",
                            "autoRotate"              : "yes",
                            "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                            "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                            "fullScreenVideo"         : "no",
                            "transitionType"          : "fade", // fade or slide
                            "transitionSpeed"         : 3,
                            "transitionDelay"         : 5,
                            "onImageLoad"             : function() { },
                            "allImagesLoaded"         : function() { },
                            "onThumbnailLoad"         : function() { },
                            "allThumbnailsLoaded"     : function() { },
                            "onTransitionStart"       : function() { },
                            "onTransitionEnd"         : function() { 
                            							if($(".rs-gallery-title").hasClass("hidden") && $(".rs-gallery-caption").hasClass("hidden")){
                                                       		$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").addClass("empty");
                                                       } else {
                                                       		$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").removeClass("empty");
                                                       }
                            },
                            "watchVidClick"           : function() { },
                            "closeVidClick"           : function() { },
                            "allLoaded"               : function() {
                            							var getWidth = $(window).width();
														var contentWidth = $("#gb-header").width();
														var galleryDescOffset = ((getWidth-contentWidth)/2);
                                                        var gallDescTriangle = ''
                            							$(gallDescTriangle).prependTo('#hp-slideshow .rs-gallery-container .rs-gallery-desc');
                                                        $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right",galleryDescOffset);
                                                        $("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").css("left",galleryDescOffset + 12);
                                                        $("#hp-slideshow .rs-gallery-container .rs-gallery-control.next").css("left",galleryDescOffset + 72);
                                                        $( window ).resize(function() {
                                                        	var getWidth = $(window).width();
															var contentWidth = $("#gb-header").width();
															var galleryDescOffset = ((getWidth-contentWidth)/2);
                                                       		$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right",galleryDescOffset);
                                                            $("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").css("left",galleryDescOffset + 12);
                                                        	$("#hp-slideshow .rs-gallery-container .rs-gallery-control.next").css("left",galleryDescOffset + 72);
                                                       });
                                                       if($(".rs-gallery-title").hasClass("hidden") && $(".rs-gallery-caption").hasClass("hidden")){
                                                       		$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").addClass("empty");
                                                       } else {
                                                       		$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").removeClass("empty");
                                                       }
                                                       $("#hp-slideshow .rs-gallery-container .rs-gallery-images ul.rs-gallery-images-list li").each(function(){
                                                       		var overlay = '';
                                                            if($(".watch-btn",this).length){
                                                            	$(overlay).insertBefore(".watch-btn",this);
                                                            } else {
                                                            	$(this).append(overlay);
                                                            }
                                                       });

                                                       $(".rs-gallery-control").wrapAll('
');
                                                       $(".rs-gallery-control.play-pause").insertBefore(".rs-gallery-control.next");
                                                       //$("#hp-slideshow .rs-gallery-container .rs-gallery-control-outer").prependTo("#hp-slideshow .rs-gallery-container .rs-gallery-desc");
                                                       $("#hp-slideshow .rs-gallery-container .rs-gallery-control-outer").insertBefore("#hp-slideshow .rs-gallery-container .rs-gallery-desc .rs-gallery-title");
                           }

			        });
			}
		});
	}

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}


		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("#hp-content div.ui-widget-header").off("click").click(function(){
        	if(!$(this).parent().hasClass("active")){
            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
            if(!$("#hp-content .ui-widget.app").last().hasClass("active")) {
                $("#hp-content-row-three-outer").css("margin-bottom","70px");
            } else {
                $("#hp-content-row-three-outer").css("margin-bottom","0px");
            }
		});
        $("#hp-slideshow-content div.ui-widget-header").off("click").click(function(){
        	if(!$(this).parent().hasClass("active")){
            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
		});
        head.ready("appScroller", function() {
        	var windowWidth = $(window).width();
        	if(windowWidth >= 480) {
                if($(".hp").length) {
                        $("div.ui-widget.app.upcomingevents").appScroller({
                                "appParent"             : ".hp",
                                "targetApp"             : "upcomingevents", //upcomingevents or headlines
                                "imageNum960"           : 5, //NUMBER OF IMAGES TO DISPLAY AT 960
                                "imageNum768"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 768
                                "imageNum640"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 640
                                "imageNum480"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 480
                                "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                                "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                                "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                                "buttonColor"           : "#c0c0c0", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                                "extraBackIconClass"    : "",
                                "extraNextIconClass"    : "",
                                "controlGutter"         : "" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
                        });
                }
            }    
        });
        if($("#hp-content .ui-widget.app").last().hasClass("active")) {
        	$("#hp-content-row-three-outer").css("margin-bottom","70px;");
        } else {
        	$("#hp-content-row-three-outer").css("margin-bottom","0px;");
        }
	}	

