    function searchControls() {
    	$("#sw-search-input").attr("tabindex","-1");
        $("#sw-search-button").attr("tabindex","-1");
        $("#gb-search button").click(function(e){
            e.preventDefault();
            if($("#gb-quicklinks button.open").length){
                	animateQuicklinks();
                }
            if($("#gb-options button.open").length){
                animateOptions();
            }
            if($("#gb-translate button.open").length){
                animateTranslate();
            }
            animateSearch();
        });

        $("#gb-search button").on('keypress', function(e) { // hooking into the default button trigger so we can stop it and run it only on keyup since we auto focus the search bar
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    e.preventDefault();
                }
            })
        .on('keyup', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                e.preventDefault();
                if($("#gb-quicklinks button.open").length){
                	animateQuicklinks();
                }
                if($("#gb-options button.open").length){
                	animateOptions();
                }
                if($("#gb-translate button.open").length){
                	animateTranslate();
                }
                animateSearch();
            }
        });

        var searchTimer = 0;
        $("#gb-search-inner").mouseenter(function() {
        	clearTimeout(searchTimer);
        });
        $("#gb-search-inner").mouseleave(function() {
        	searchTimer = setTimeout(function(){
            	$("#gb-search-inner").css("right","100%");
                $("#gb-search button").attr("aria-label", "Open search").removeClass("open");
                $("#gb-search-inner").removeClass("open");
                $("#sw-mystart-search label, #sw-search-input").attr("aria-hidden", "true");
                $("#sw-search-input, #sw-search-button").attr("tabindex","-1");
                $("#gb-search button").focus();
            }, 1000);
        });
	}

    function animateSearch() {
        if(!$("#gb-search-inner").hasClass("open")) {
            $("#gb-search button").attr("aria-label", "Close search").addClass("open");
            $("#gb-search-inner").addClass("open");
            $("#sw-mystart-search label, #sw-search-input").attr("aria-hidden", "false");
            $("#sw-search-input, #sw-search-button").attr("tabindex","0");

            $("#gb-search-inner").css("right","-260px");
            //$("#sw-search-input").focus();
        } else {
        	$("#gb-search-inner").css("right","100%");
            $("#gb-search button").attr("aria-label", "Open search").removeClass("open");
            $("#gb-search-inner").removeClass("open");
            $("#sw-mystart-search label, #sw-search-input").attr("aria-hidden", "true");
            $("#sw-search-input, #sw-search-button").attr("tabindex","-1");
            $("#gb-search button").focus();
        }
    }    

    function quicklinksControls() {
    	$(".global-icon").attr("tabindex","-1");
    	$("#gb-quicklinks button").click(function(e){
            e.preventDefault();
            if($("#gb-search button.open").length){
                	animateSearch();
                }
            if($("#gb-options button.open").length){
                animateOptions();
            }
            if($("#gb-translate button.open").length){
                animateTranslate();
            }
            animateQuicklinks();
        });
        $("#gb-quicklinks button").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                if($("#gb-search button.open").length){
                    animateSearch();
                }
                if($("#gb-options button.open").length){
                    animateOptions();
                }
                if($("#gb-translate button.open").length){
                    animateTranslate();
                }
                animateQuicklinks();
            }
        });
        var qlTimer = 0;
        $("#gb-quicklinks-inner").mouseenter(function() {
        	clearTimeout(qlTimer);
        });
        $("#gb-quicklinks-inner").mouseleave(function() {
        	qlTimer = setTimeout(function(){
            	$("#gb-quicklinks-inner").css("right","100%");
                $("#gb-quicklinks button").attr("aria-label", "Open Quicklinks menu").removeClass("open");
                $("#gb-quicklinks-inner").removeClass("open");
                $("#gb-quicklinks button").focus();
                $("#gb-quicklinks-inner .global-icon, #gb-quicklinks-inner .close").attr("tabindex","-1");
            }, 1000);
        });

        $("#gb-quicklinks-inner .global-icon").focus(function(e){
        	$(".icon", this).css("color","#FFF");
        });
        $("#gb-quicklinks-inner .global-icon").focusout(function(e){
        	$(".icon", this).css("color","rgba(255,255,255,0.6)");
        });
        $("#gb-quicklinks-inner .close").click(function(e){
            e.preventDefault();
            animateQuicklinks();
        });

        $("#gb-quicklinks-inner .close").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateQuicklinks();
            }
        });

        $("#gb-quicklinks-inner a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).is(":first-child")) {
                        // FOCUS LAST ITEM
                        $("#gb-quicklinks-inner a:last-child").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).prev("a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).parent().find("a:first-child").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).next("a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#gb-quicklinks-inner a:first-child").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#gb-quicklinks-inner a:last-child").focus();
                break;
            }
        });

    }

    function animateQuicklinks() {
    	if(!$("#gb-quicklinks-inner").hasClass("open")) {
            $("#gb-quicklinks button").attr("aria-label", "Close Quicklinks menu").addClass("open");
            $("#gb-quicklinks-inner").addClass("open");

            $("#gb-quicklinks-inner").css("right","-836px");
            $("#gb-quicklinks-inner .global-icon").first().focus();
            $("#gb-quicklinks-inner .global-icon, #gb-quicklinks-inner .close").attr("tabindex","0");
        } else {
        	$("#gb-quicklinks-inner").css("right","100%");
            $("#gb-quicklinks button").attr("aria-label", "Open Quicklinks menu").removeClass("open");
            $("#gb-quicklinks-inner").removeClass("open");
            $("#gb-quicklinks button").focus();
            $("#gb-quicklinks-inner .global-icon, #gb-quicklinks-inner .close").attr("tabindex","-1");
        }
    }

    function optionsControls() {
    	$("#gb-options-inner li a").attr("tabindex","-1");
    	$("#gb-options button").click(function(e){
            e.preventDefault();
            if($("#gb-search button.open").length){
                	animateSearch();
                }
            if($("#gb-quicklinks button.open").length){
                animateQuicklinks();
            }
            if($("#gb-translate button.open").length){
                animateTranslate();
            }
             animateOptions();
        });
        $("#gb-options button").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                if($("#gb-search button.open").length){
                	animateSearch();
                }
                if($("#gb-quicklinks button.open").length){
                    animateQuicklinks();
                }
                if($("#gb-translate button.open").length){
                    animateTranslate();
                }
                animateOptions();
            }
        });

        $(".gb-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".gb-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".gb-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".gb-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".gb-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });

        var optionsTimer = 0;
        $("#gb-options-inner").mouseenter(function() {
        	clearTimeout(optionsTimer);
        });
        $("#gb-options-inner").mouseleave(function() {
        	optionsTimer = setTimeout(function(){
            	$("#gb-options-inner").css("right","100%");
                $("#gb-options button").attr("aria-label", "Open User Options Menu").removeClass("open");
                $("#gb-options-inner").removeClass("open");
               	$("#gb-options-inner li a").attr("tabindex","-1");
                $("#gb-options button").focus();
            }, 1000);
        });
    }

    function animateOptions() {
    	if(!$("#gb-options-inner").hasClass("open")) {
            $("#gb-options button").attr("aria-label", "Close User Options menu").addClass("open");
            $("#gb-options-inner").addClass("open");

            $("#gb-options-inner").css("right","-260px");
            $("#gb-options-inner li a").first().focus();
            $("#gb-options-inner li a").attr("tabindex","0");
        } else {
        	$("#gb-options-inner").css("right","100%");
            $("#gb-options button").attr("aria-label", "Open User Options menu").removeClass("open");
            $("#gb-options-inner").removeClass("open");
            $("#gb-options button").focus();
            $("#gb-options-inner li a").attr("tabindex","-1");
        }
    }

