		if("803-476-8000" != "") {
			$(".gb-footer-contact.phone").removeClass("hidden");
		}
		//GET SCHOOLWIRES FOOTER LINKS
		//var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var swURL = $("#sw-footer-logo a").attr("href");

		//$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	
		$("a#swlogo").attr("href", swURL);	

        if ( $('.fax-number').text().length == 0 ) { //added if statement by Lyndsay - Case 00493811 - 2.23.17
            $('.gb-footer-contact.fax').css('display', 'none');
        }
	}

    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}


	function modEvents(){
		head.ready("modEvents", function() {




			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "no",
					monthLong   : "yes",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
        /*
          This should be placed within the eventsByDay function right after that functions loop.
        */

        $(".ui-article", container).each(function(){
        	if(!$('h1.ui-article-title', this).length){
            	var currentDate = $(this).parent().prev('li').find('.ui-article-title').html();
                $(this).prepend('
' + currentDate + '');
            }


        	if(!$('h1.ui-article-title.same-date', this).length) {
                  var newDateTime = $('h1.ui-article-title', this).html().toLowerCase().split("");
                  var newDate = '';
                  var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                  //add a zero to the date if it is a single digit
                  if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                  //if we have the day of the week make sure to include it.
                  if( dateIndex > 1 ){ 
                      newDate += newDateTime[0] + "";
                  }

                  //add in the month
                  newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                  //wrap the date in a new tag
                  newDate += '' + newDateTime[dateIndex] + '';

                  //append the date and month back into their columns
                  $('h1.ui-article-title', this).html(newDate);	
			}

              //add an ALL DAY label if no time was given
              /*$('.ui-article-description', this).each(function(){
                  if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                      $(this).prepend('ALL DAY');
                  }
              });*/
          });

          $(".ui-widget.app.upcomingevents li").each(function() {
          		// CHECK FOR TIME
            if($(".sw-calendar-block-time", this).length) {
            	$(".ui-article-description", this).append($(".sw-calendar-block-time", this));
            } else {
            	$(".ui-article-description", this).append('ALL DAY');
            }
          });
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
