		}
	}

	function buildStyleSheet(){
		var primaryColor15 = "rgba(" + hexToRgb('#444444') + ", .15)";
        var photoGalleryBG50 = "rgba(" + hexToRgb('#dbbb66') + ", .5)";

		var secondaryColor = "#dbbb66";
		secondaryColor = secondaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow .rs-gallery-container .rs-gallery-desc {' +
					'	border: 2px solid ' + primaryColor15 + ';' +
                    '}' +
                    '#hp-slideshow-outer:before {' +
                    '	background: ' + photoGalleryBG50 + ';' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function accessibility() {

    	$("#gb-mystart button").each(function(){
        	$(this).attr({"aria-expanded":"false","aria-haspopup":"true"});
        });

        $("#gb-mystart > div > div").each(function(){
            $(this).attr("aria-hidden","true");
        });

        $("#gb-mystart button").click(function(){
        	if(!$(this).hasClass("open")) {
            	$(this).attr({"aria-expanded":"true","aria-haspopup":"false"});
                $("+ div",this).attr("aria-hidden","false");
            } else {
            	$(this).attr({"aria-expanded":"false","aria-haspopup":"true"});
                $("+ div",this).attr("aria-hidden","true");
            }
        });
        $("#gb-mystart button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                case csKeyCodes.space:
                case csKeyCodes.enter:
                	e.preventDefault();
                	// CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                    if(!$(this).hasClass("open")) {
                        $(this).attr({"aria-expanded":"true","aria-haspopup":"false"});
                        $("+ div",this).attr("aria-hidden","false");
                    } else {
                        $(this).attr({"aria-expanded":"false","aria-haspopup":"true"});
                        $("+ div",this).attr("aria-hidden","true");
                    }

                break;
            }
        });


        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(this).removeAttr("role");
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1)).attr("role","button").attr("aria-expanded","false").attr("aria-haspopup","true").removeAttr("aria-hidden");
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(".sw-dropdown-list li:first-child a", this).focus();
                break;
                case csKeyCodes.space:
                case csKeyCodes.enter:
                	e.preventDefault();
                	// CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                    if(!$(this).hasClass("open")) {
                        // TOGGLE THE DROPDOWN
                        $(".sw-dropdown-arrow", this).addClass("open");
                        $(this).addClass("open");
                        $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".selector",this).attr("aria-expanded","true");
                        $(".sw-dropdown-list li a", this).removeAttr("tabindex");
                        $(".sw-dropdown-list li:first-child a", this).focus();
                    } else {
                    	$(".sw-dropdown-arrow", this).removeClass("open");
                        $(this).removeClass("open");
                        $(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".selector",this).attr("aria-expanded","false");
                    	$(this).focus();
                    }

                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        });

        $("#sw-myaccount").focus(function(e) {
                  // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                  if(!$(this).hasClass("open")) {
                      // TOGGLE THE DROPDOWN
                      $(".icon-font",this).toggleClass("open");
                        var txt = $(".icon-font.open", this).is(':visible') ? '_' : '+';
                        $(".icon-font", this).text(txt);
                      $(this).parent().toggleClass("open");
                      $("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");
                  }
              });

        $("#cs-sw-myaccount-list").focusout(function() {
          var $this = $(this);

          // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
          setTimeout(function() {
              // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
              if(!$this.is(":focus") && !$this.find(":focus").length) {
                  // TOGGLE THE DROPDOWN
                  $(".icon-font",this).toggleClass("open");
                var txt = $(".icon-font.open", this).is(':visible') ? '_' : '+';
                $(".icon-font", this).text(txt);
                  $this.toggleClass("open");
                  $("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");
              }
          }, 10);
        });

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });

    }

