    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "HeaderIsSticky": false,
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.Header();
            this.StickyHeader();
            this.ChannelBar();
            this.Body();
            this.ModEvents();
            this.AppScroller();
            this.Footer();
            this.DynStyles();
            this.Slideshow();
            this.SocialIcons();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Search();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){ _this.WindowScroll(); });

			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyHeader();
        },

        "WindowScroll": function() {
            this.StickyHeader();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "MyStart": function() {
