District Home');
                }
            	$(".cs-mystart-dropdown.schools .cs-dropdown-list li a").removeAttr("tabindex");
            }
            $(".sw-mystart-button.home").insertAfter(".gb-header-right.top .cs-mystart-dropdown.schools");

        	// ADD TRANSLATE - changed to google branded by Matt - case#3100630 - 03.22.18
        	$(".gb-header-right.top").creativeTranslate({
                "type": 2,
                "languages": [
                	["Spanish", "Espaol", "es"],
                    ["Arabic", "", "ar"],
                    ["Chinese Simplified", "", "zh-CN"],
					["Chinese Traditional", "", "zh-TW"],
                    ["French", "franais", "fr"],
