            });

            $(".cs-mystart-dropdown.translate .cs-dropdown ul").attr("class", "cs-dropdown-list");

            this.MyStartDropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "MyStartDropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

        	var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(document).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(document).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:


                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
				$(dropdownList + " a", this).attr("tabindex", "-1");
				$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/SC02205954/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            // ADD STICKY LOGO
            var stickyLogoSrc = $.trim("/cms/lib/SC02205954/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var stickySrcSplit = stickyLogoSrc.split("/");
            var stickySrcSplitLen = stickySrcSplit.length;
            if((stickyLogoSrc != "") && (stickySrcSplit[stickySrcSplitLen - 1] != "default-man.jpg")) {
                $("#gb-sticky-logo").append('');
            } else {
                $("#gb-sticky-logo").append('');
            }

            // ADD SITENAME
            var showSiteName = false;
            var siteNameBegin = "", siteNameEnd = "";
            var siteNameOne = $.trim("");
            var siteNameTwo = $.trim("");
            if(showSiteName) {
                if((siteNameOne == "") && (siteNameTwo == "")) {
                    var splitLen = 2;
                    var siteName = "Spartanburg School District 7";
                    siteName = siteName.split(" ");
                    var siteNameLength = siteName.length;
                    if(siteNameLength > 2){
                        siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                    } else {
                        siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                    }
                    siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                    $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "").removeClass("hidden");
                } else if((siteNameOne != "") && (siteNameTwo == "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "").removeClass("hidden");
                } else if((siteNameOne == "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameTwo + "").removeClass("hidden");
                } else if((siteNameOne != "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "").removeClass("hidden");
                }
            }
        },

        "StickyHeader": function() {
            switch(this.GetBreakPoint()) {
                case "desktop":
                    var mainOffSet = $("main").offset().top;
                    if ($(window).scrollTop() <= mainOffSet) {
                        if(this.HeaderIsSticky) {
                            // HEADER
                            if(true) {
                            	$("#gb-logo").removeClass("hidden").attr("aria-hidden", "false");
                            }
                            if(false) {
                            	$("#gb-sitename").removeClass("hidden").attr("aria-hidden", "false");
                            }
                            $(".gb-header-right.top").removeClass("hidden").attr("aria-hidden", "false");
                            $("#gb-sticky-logo").addClass("hidden").attr("aria-hidden", "true");
							$("header").removeClass("sticky").removeAttr("style");

                            // SOCIAL ICONS
                            $("#gb-social-icons-outer").removeClass("sticky");
                            $("#gb-social-icons").removeAttr("style");

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                    } else {
                        $("header").css("height", $("header").outerHeight(true));

                        if(!this.HeaderIsSticky) {
                            // HEADER
                            $("header").addClass("sticky");
                            if(true) {
                            	$("#gb-logo").addClass("hidden").attr("aria-hidden", "true");
                            }
                            if(false) {
                            	$("#gb-sitename").addClass("hidden").attr("aria-hidden", "true");
                            }
                            $(".gb-header-right.top").addClass("hidden").attr("aria-hidden", "true");
                            $("#gb-sticky-logo").removeClass("hidden").attr("aria-hidden", "false");

                            // SOCIAL ICONS
                            $("#gb-social-icons-outer").addClass("sticky");
                            $("#gb-social-icons").css("top", $("header.sticky #gb-header").outerHeight(true) + 44);

                            this.HeaderIsSticky = true;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            if($(".wcm-controls").hasClass("wcm-stuck")) {
                                $(".wcm-controls").css("margin-top", $("#gb-header").outerHeight(true));
                            }
                        }
                    }

                    $("#sw-maincontent").css({
                        "display": "block",
                        "position": "relative",
                        "top": "-" + $("#gb-header").outerHeight(true) + "px"
                    });
                break;
                case "768": case "640": case "480": case "320":
                    if(this.HeaderIsSticky) {
                        // HEADER
                        if(true) {
                            $("#gb-logo").removeClass("hidden").attr("aria-hidden", "false");
                        }
                        if(false) {
                            $("#gb-sitename").removeClass("hidden").attr("aria-hidden", "false");
                        }
                        $(".gb-header-right.top").removeClass("hidden").attr("aria-hidden", "false");
                        $("#gb-sticky-logo").addClass("hidden").attr("aria-hidden", "true");
                        $("header").removeClass("sticky").removeAttr("style");

                        // SOCIAL ICONS
                        $("#gb-social-icons-outer").removeClass("sticky")
                        $("#gb-social-icons").removeAttr("style");

                        this.HeaderIsSticky = false;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        $(".wcm-controls").css("margin-top", "0px");
                    }
                    $("#sw-maincontent").removeAttr("style");
                break;
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function() {
            	if($("> ul.sw-channel-dropdown > li", this).length) {
                	$(this).attr("data-has-dropdown", "true");
                }
            });
        },

