        "ModEvents": function() {
            $(".upcomingevents").modEvents({
                columns     : "no",
                monthLong   : "no",
                dayWeek     : "no"
            });

            $(".upcomingevents .ui-article").each(function(){
            	if(!$(".ui-article-description .sw-calendar-block-time", this).length) {
                	$(".ui-article-description", this).prepend('All Day');
                }

                var blockTitle = $(".sw-calendar-block-title a", this).text();
                $(".sw-calendar-block-title a", this).html('' + blockTitle + '');

                if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                    var moveArticle = $(this).html();
                    $(this).parent().prev().find(".ui-article").append(moveArticle);
                    $(this).parent().remove();
                };
            });
        },

        "AppScroller": function() {
        	var template = this;

            function HpSlider(app, slideMax, sliderIndex) {
            	var _this = this;

                // SET VARIABLES
                this.app = app;
                this.appId = $(this.app).attr("id").replace("pmi-", "");
                var photoGallery = eval("photoGallery" + this.appId);
                this.slideNum = photoGallery.Album.Images.length;
                this.breakpoints = ["desktop", "768", "640", "480", "320"];
                this.headlineSlideMax = slideMax;
                this.animationDelay = 500;
                this.WindowWidth = $(window).width();

                // GATHER DATA
                var records = "";
                $.each(photoGallery.Album.Images, function(index, photo) {
                	var imageRawSource = photo.src.split("?");
                    var imageSource = photoGallery.Album.lgpath + imageRawSource[0];
                    var linkWrapStart = ($.trim(photo.link) != "") ? '' : '';
                    var linkWrapEnd = ($.trim(photo.link) != "") ? '' : '';
                    var caption = ($.trim(photo.caption) != "") ? '' + photo.caption + '' : '';

                    // ADD RECORD
                	records +=  '
' +
                    				'
' +
                    					linkWrapStart + 
                                    		'' +
                                            caption +
                                    	linkWrapEnd + 
                                    '' +
                                '';
                });

                // ADJUST STRUCTURE
                var header = ""

                $(".ui-widget.app", this.app).append('' +
                	'
' +
                    '
' +
                    	'' +
                    	'
' +
                    		'' +
                            	records +
                            '' +
                    	'' +
                        '' +
                    '' +
                '');

                // SET ATTRIBUTES AND ALIGNMENT
                this.CheckSlider();

                // BIND EVENTS
                this.Events();

                // RESET ATTRIBUTES AND ALIGNMENT ON WINDOW RESIZE
                $(window).resize(function() {
                	// SCROLLING ON iOS TRIGGERS THE WINDOW RESIZE EVENT SO WE WILL MAKE SURE THE WINDOW HAS ACTUALLY CHANGED BEFORE TRIGGERING
                	if($(window).width() != _this.WindowWidth) {
                		_this.CheckSlider();

                        _this.WindowWidth = $(window).width();
                    }
                });
            }

            HpSlider.prototype.Events = function() {
            	var _this = this;

                // CLICK AND KEYDOWN
                $(this.app).on("click keydown", ".hp-slider-btn", function(e) {
                    if(template.AllyClick(e)) {
                        e.preventDefault();

                        if(!$(".ui-widget.app", _this.app).hasClass("animating")) {
                            // BACK BUTTON WAS CLICKED
                            if($(this).hasClass("back")) {
                                cycleBack();
                            }

                            // NEXT BUTTON WAS CLICKED
                            else {
                            	cycleNext();
                            }
                        }
                    }
                });

                // SWIPE RIGHT
                $(this.app).on("swiperight", function(e) {
                    e.preventDefault();
                    if(!$(".ui-widget.app", _this.app).hasClass("animating")) {
                    	cycleBack();
                    }
                });

                // SWIPE LEFT
                $(this.app).on("swipeleft", function(e) {
                    e.preventDefault();
                    if(!$(".ui-widget.app", _this.app).hasClass("animating")) {
                    	cycleNext();
                    }
                });

                // HOVER AND FOCUS
                $(this.app).on("focusin", ".ui-widget.app", function() {
                	$(this).addClass("focus");
                }).on("focusout", ".ui-widget.app", function() {
                	$(this).removeClass("focus");
                });

                function cycleBack() {
                	// SET VARIABLES
                    var breakpointIndex = _this.breakpoints.indexOf(template.GetBreakPoint());
                    var slideWidth = $(".ui-articles > li[aria-hidden='false']", _this.app).outerWidth(true);
                    var slideMax = 0;

                    slideMax = _this.headlineSlideMax[breakpointIndex];

                    // DISABLE THE BUTTONS TEMPORARILY
                    $(".ui-widget.app", _this.app).addClass("animating");

                    // FIRST CHILD IS VISIBLE
                    if($(".ui-articles > li:first-child", _this.app).attr("aria-hidden") == "false") {
                        // ANIMATE TO LAST CHILD
                        $(".ui-articles > li", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"}).removeClass("first last");
                        $(".ui-articles", _this.app).css("left", "-" + ((_this.slideNum - slideMax) * slideWidth) + "px");

                        // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                        setTimeout(function() {
                            $(".ui-articles > li:nth-child(-n+" + (_this.slideNum - slideMax) + ")", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});
                            $(".ui-articles > li[data-visibility='hidden'] a", _this.app).attr("tabindex", "-1");
                            $(".ui-articles > li[data-visibility='visible'] a", _this.app).attr("tabindex", "0");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).first().addClass("first");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).last().addClass("last");

                            // ENABLE THE BUTTONS
                            $(".ui-widget.app", _this.app).removeClass("animating");
                        }, _this.animationDelay);
                    }

                    // ONE SLIDE BACKWARD
                    else {
                    	$(".ui-articles > li", _this.app).removeClass("first last");
                        $(".ui-articles > li[data-visibility='visible']", _this.app).first().prev("li").attr({"aria-hidden": "false", "data-visibility": "visible"});
                        $(".ui-articles", _this.app).css("left", (Number($(".ui-articles", _this.app).css("left").replace("px", "")) + slideWidth).toFixed(5) + "px");

                        // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                        setTimeout(function() {
                            $(".ui-articles > li[data-visibility='visible']", _this.app).last().attr({"aria-hidden": "true", "data-visibility": "hidden"});
                            $(".ui-articles > li[data-visibility='hidden'] a", _this.app).attr("tabindex", "-1");
                            $(".ui-articles > li[data-visibility='visible'] a", _this.app).attr("tabindex", "0");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).first().addClass("first");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).last().addClass("last");

                            // ENABLE THE BUTTONS
                            $(".ui-widget.app", _this.app).removeClass("animating");
                        }, _this.animationDelay);
                    }
                }

                function cycleNext(button) {
                	// SET VARIABLES
                    var breakpointIndex = _this.breakpoints.indexOf(template.GetBreakPoint());
                    var slideWidth = $(".ui-articles > li[aria-hidden='false']", _this.app).outerWidth(true);
                    var slideMax = 0;

                    slideMax = _this.headlineSlideMax[breakpointIndex];

                    // DISABLE THE BUTTONS TEMPORARILY
                    $(".ui-widget.app", _this.app).addClass("animating");

                    // LAST CHILD IS VISIBLE
                    if($(".ui-articles > li:last-child", _this.app).attr("aria-hidden") == "false") {
                        // ANIMATE TO FIRST CHILD
                        $(".ui-articles > li", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"}).removeClass("first last");
                        $(".ui-articles", _this.app).css("left", "0px");

                        // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                        setTimeout(function() {
                            $(".ui-articles > li:nth-child(n+" + (slideMax + 1) + ")", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});
                            $(".ui-articles > li[data-visibility='hidden'] a", _this.app).attr("tabindex", "-1");
                            $(".ui-articles > li[data-visibility='visible'] a", _this.app).attr("tabindex", "0");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).first().addClass("first");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).last().addClass("last");

                            // ENABLE THE BUTTONS
                            $(".ui-widget.app", _this.app).removeClass("animating");
                        }, _this.animationDelay);
                    }

                    // ONE SLIDE FORWARD
                    else {
                    	$(".ui-articles > li", _this.app).removeClass("first last");
                        $(".ui-articles > li[data-visibility='visible']", _this.app).last().next("li").attr({"aria-hidden": "false", "data-visibility": "visible"});
                        $(".ui-articles", _this.app).css("left", (Number($(".ui-articles", _this.app).css("left").replace("px", "")) - slideWidth).toFixed(5) + "px");

                        // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                        setTimeout(function() {
                            $(".ui-articles > li[data-visibility='visible']", _this.app).first().attr({"aria-hidden": "true", "data-visibility": "hidden"});
                            $(".ui-articles > li[data-visibility='hidden'] a", _this.app).attr("tabindex", "-1");
                            $(".ui-articles > li[data-visibility='visible'] a", _this.app).attr("tabindex", "0");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).first().addClass("first");
                            $(".ui-articles > li[data-visibility='visible']", _this.app).last().addClass("last");

                            // ENABLE THE BUTTONS
                            $(".ui-widget.app", _this.app).removeClass("animating");
                        }, _this.animationDelay);
                    }
                }
            }

            HpSlider.prototype.CheckSlider = function() {
            	var _this = this;
                var breakpointIndex = this.breakpoints.indexOf(template.GetBreakPoint());
                var slideMax = 0;

                // DISABLE THE BUTTONS TEMPORARILY
                $(".ui-widget.app", _this.app).addClass("animating");

                $(".ui-articles > li", this.app).attr({"aria-hidden": "false", "data-visibility": "visible"}).removeClass("first last");  
                $(".ui-articles", this.app).css("left", "0px");

                slideMax = this.headlineSlideMax[breakpointIndex];

                // USE SET TIMEOUT TO UPDATE ATTRIBUTES BECAUSE ANIMATION IS HANDLED VIA CSS
                setTimeout(function() {
                    $(".ui-articles > li", _this.app).attr({"aria-hidden": "true", "data-visibility": "hidden"});
                    $(".ui-articles > li:nth-child(-n+" + _this.headlineSlideMax[breakpointIndex] + ")", _this.app).attr({"aria-hidden": "false", "data-visibility": "visible"});
                    $(".ui-articles > li[data-visibility='hidden'] a", _this.app).attr("tabindex", "-1");
                    $(".ui-articles > li[data-visibility='visible'] a", _this.app).attr("tabindex", "0");
                    $(".ui-articles > li[data-visibility='visible']", _this.app).first().addClass("first");
                    $(".ui-articles > li[data-visibility='visible']", _this.app).last().addClass("last");

                    // ENABLE THE BUTTONS
                    $(".ui-widget.app", _this.app).removeClass("animating");
                }, _this.animationDelay);

                if(this.slideNum > slideMax) {
                    $(".hp-slider-btn", this.app).removeClass("hidden");
                } else {
                    $(".hp-slider-btn", this.app).addClass("hidden");
                }
            }

            // REMOVE INAPPROPRIATE APPS AND EXCESS GALLERIES
            $(".hp-content.bottom .ui-widget.app").each(function() {
            	if(!$(this).hasClass("gallery json")) {
                	$(this).closest('div[id*="pmi-"]').remove();
                }
            });
            $(".hp-content.bottom .region > div:nth-child(n+4)").remove();

            // INITIALIZE SLIDERS IN THIS WAY TO MAKE THEM UNIQUE
            var sliders = {}, sliderIndex = 0;
            $(".hp-content.bottom .ui-widget.app").each(function() {
            	var slideMax = ($(this).closest('div[id*="pmi-"]').is(":last-child")) ? [3, 3, 2, 2, 1] : [4, 3, 2, 2, 1];

            	sliders[sliderIndex] = new HpSlider($(this).closest('div[id*="pmi-"]'), slideMax, sliderIndex);

                sliderIndex++;
            });

            // ADD SLIDE LEADER TEXT
            $(".hp-content.bottom .region > div:nth-child(1) div.ui-widget.app.gallery.json .cs-slide-leader").html('' + $.trim("") + '' + $.trim("Inspire"));
			$(".hp-content.bottom .region > div:nth-child(2) div.ui-widget.app.gallery.json .cs-slide-leader").html('' + $.trim("") + '' + $.trim("Equip"));
			$(".hp-content.bottom .region > div:nth-child(3) div.ui-widget.app.gallery.json .cs-slide-leader").html('' + $.trim("Moment For") + '' + $.trim("Mission"));
        },

