        "HexToRgb": function(h) {
            var r = parseInt((this.CutHex(h)).substring(0,2),16);
            var g = parseInt((this.CutHex(h)).substring(2,4),16);
            var b = parseInt((this.CutHex(h)).substring(4,6),16);
            return r + ', ' + g + ', ' + b;
        },

        "CutHex": function(h) {
        	return (h.charAt(0)=="#") ? h.substring(1,7):h;
        },

