						viewWidth = parseInt(viewWidth);
						var newPos = slidePos * viewWidth;

						// SET THE CSS PROPERTIES
						$(".rs-gallery-container", element).css({"height": viewHeight + "px"});
						$(".rs-gallery-container .rs-gallery-images", element).css({"height": viewHeight + "px"});
						$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).css({"width": sliderWidth + "px", "height": viewHeight + "px", "left": "-" + newPos + "px"});
