link.script:
    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": false,
        "ChannelBarIsSticky": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
			this.MyStart();
            this.JsMediaQueries();
			this.SchoolList();
			this.Translate();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyChannelBar();
            if($(".hp").length) {
              this.Slideshow();
              this.CheckSlideshow();
            }
            this.Body();
            this.ShortcutsFull();
            this.ModEvents();
            if($(".hp-content.one .ui-widget.app.upcomingevents li:not(.no-events) .ui-article").length || $(".hp-content.two .ui-widget.app.upcomingevents li:not(.no-events) .ui-article").length || $(".hp-content.three .ui-widget.app.upcomingevents li:not(.no-events) .ui-article").length) {
            	this.TabbedEvents();
            }
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			csGlobalJs.OpenInNewWindowWarning();
            if($(".hp").length) {
            	this.hpCustomHeadlines();
            }
            //REMOVE EMPTY APP FOOTERS
            $(".ui-widget.app").each(function() {
            	var thisApp = this;
                var thisAppFooter = $(thisApp).find(".ui-widget-footer").height();
                if(thisAppFooter < 1) {
                	$(thisApp).find(".ui-widget-footer").remove();
                }
            });
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyChannelBar();
            if($(".hp").length) {
            	this.hpCustomHeadlines();
                this.CheckSlideshow();
            }
        },

        "WindowScroll": function() {
            this.StickyChannelBar();
            if($(".hp").length) {
                this.CheckSlideshow();
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":
					if($("#sw-mystart-mypasskey").length) {
                    	if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
                        	$("#sw-mystart-mypasskey").appendTo("#gb-channel-list");
                        }
                    }
                break;
                case "640":
					if($("#sw-mystart-mypasskey").length) {
                    	if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
                        	$("#sw-mystart-mypasskey").appendTo("#gb-channel-list");
                        }
                    }
                break;
                case "480":
					if($("#sw-mystart-mypasskey").length) {
                    	if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
                        	$("#sw-mystart-mypasskey").appendTo("#gb-channel-list");
                        }
                    }
                break;
                case "320":
					if($("#sw-mystart-mypasskey").length) {
                    	if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
                        	$("#sw-mystart-mypasskey").appendTo("#gb-channel-list");
                        }
                    }
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$(".gb-header.right").prepend($(".sw-mystart-button.home"));
            }

            if(!$(".sw-mystart-button.home").length && !$("#sw-mystart-mypasskey").length && $(".cs-mystart-dropdown.schools").length) {
            	$(".cs-mystart-dropdown.schools").addClass("no-border");
            }
            if(!$(".sw-mystart-button.home").length && $("#sw-mystart-mypasskey").length && $(".cs-mystart-dropdown.schools").length) {
            	$(".cs-mystart-dropdown.schools").addClass("no-border");
            }
            if(!$(".sw-mystart-button.home").length && $("#sw-mystart-mypasskey").length && !$(".cs-mystart-dropdown.schools").length) {
            	$("#sw-mystart-mypasskey").addClass("no-border");
            }

            $(".sw-mystart-button.home").hover(function() {
            	$(".gb-header.right").addClass("home-hover");
            }, function() {
            	$(".gb-header.right").removeClass("home-hover");
            });

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

		"SchoolList": function() {
			// ADD SCHOOL LIST
			if(this.ShowSchoolList) {
				var schoolDropdown =	'
' +
											'
Our Schools' +
											'
' +
												'' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
											'' +
										'';

				// ADD SCHOOL LIST TO THE DOM	
				$(".gb-header.right").append(schoolDropdown);

				// BIND DROPDOWN EVENTS
				this.DropdownActions({
					"dropdownParent": ".cs-mystart-dropdown.schools",
					"dropdownSelector": ".cs-selector",
					"dropdown": ".cs-dropdown",
					"dropdownList": ".cs-dropdown-list"
				});
			}
		},

