	/* START CANVAS CODE */
	function initHeaderCurve() {
		var canvas = document.getElementById("header-curve");
		var ctx = canvas.getContext("2d");

		drawHeaderCurve(ctx);
	}

	function drawHeaderCurve(ctx) {
		var alpha = ctx.globalAlpha;

			//Draws Curve
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0, 0.7);
		ctx.bezierCurveTo(0, 0.7, 20.0, 35.2, 20.0, 97.9);
		ctx.bezierCurveTo(20.0, 144.7, 0, 195.0, 0, 195.0);
		ctx.lineTo(0, 0.7);
		ctx.closePath();
		ctx.fillStyle = "#185632";
		ctx.fill();

			//Draws Border
		ctx.beginPath();
		ctx.moveTo(0, 0.7);
		ctx.quadraticCurveTo(20.0, 35.2, 20.0, 97.9);
		ctx.quadraticCurveTo(20.0, 144.7, 0.0, 195.0);
		ctx.lineWidth = 1.5;
		ctx.strokeStyle = 'rgba(255, 255, 255, 0.7)';
		ctx.stroke();

		ctx.closePath();
		ctx.restore();
	}

	function initFooterCurve() {

		var canvas = document.getElementById("footer-curve");
		var ctx = canvas.getContext("2d");

		drawFooterCurve(ctx);
	}

	function drawFooterCurve(ctx) {
		// footer curve
		ctx.beginPath();
		ctx.moveTo(258.7, 0.0);
		ctx.bezierCurveTo(410.6, -0.1, 503.9, 10.0, 503.9, 10.0);
		ctx.lineTo(0.0, 10.0);
		ctx.bezierCurveTo(0.0, 10.0, 91.3, 0.1, 258.7, 0.0);
		ctx.closePath();
		ctx.fillStyle = "#185632";
		ctx.fill();
	}

	var scrollHookOn = 0;
	var scrollHookOff = 1;
	var openMenuIcon = "icon-dbl-arrow-up";
	var closedMenuIcon = "icon-dbl-arrow-down";

	$(window).load(function(){
		searchText();
		calendarListView();

        if( $(window).width() >= 1024 ){
        	var gbHeaderOffset = $('#gb-header-outer').outerWidth() - (($(window).width() - 1280) / 2);

            if( ($(window).width() - 1280) < 0 ){
            	gbHeaderOffset = $('#gb-header-outer').outerWidth();
            }

            $('#gb-channel-list-outer').css('top', $('#gb-custom-mystart-outer').outerHeight());
        	$('#gb-channel-list').css('padding-left', gbHeaderOffset + 18);

            $('#gb-custom-mystart').css('padding-left', gbHeaderOffset + 18);
        }

        if( $('.sp').length > 0 ){
            var spPaddingTop = $('#gb-header-outer').outerHeight() - $('.sp-right-header').outerHeight();
            spPaddingTop = ( spPaddingTop <= 0 ) ? 30 : spPaddingTop += 30;
            $('#sp-content > .sp.column, #spn-content').css('padding-top', spPaddingTop);
        }

        $('#gb-channel-list li.sw-channel-item').each(function(k, v){
            $('.sw-channel-dropdown', v).css('top', $(v).position().top + $(v).outerHeight());
        });
        $('#gb-sticky-channels li.sw-channel-item').each(function(k, v){
            $('.sw-channel-dropdown', v).css('top', $(v).position().top + $(v).outerHeight());
        });

        if( $('.hp').length > 0 && $(window).width() >= 768 ){
        	hpSameHeights();
        }else if( $('.sp').length > 0 ){
        	spSameHeights();
        }
	});

