		//$("#sw-content-container1.ui-hp").tabBlocks();
		moveHeader();
		globalIcons();

		$('.gb-icon-menu .dropdown-trigger').click(function(e){ //slides top to bottom
			if( $(this).hasClass('open-menu') ){
				if($(this).hasClass('change-icon')){
					$(this).removeClass(openMenuIcon).addClass(closedMenuIcon);
				}
				$(this).removeClass('open-menu');

			}else{
				if($(this).hasClass('change-icon')){
					$(this).removeClass(closedMenuIcon).addClass(openMenuIcon);
				}
				$(this).addClass('open-menu');
			}
			$(this).siblings('.dropdown-list').animate({ height: 'toggle' });
			$('.gb-global-menu-label').animate({ width: 'toggle' });
		});

		$('.gb-icon-menu .slide-out-trigger').click(function(e){ //slides from right to left
			var clickedIcon = $(this);
			var alreadyOpen = false;
			if(clickedIcon.parent('li').hasClass('open-menu')){
				alreadyOpen = true;
			}
			if( alreadyOpen ){
				clickedIcon.siblings('.dropdown-list').animate({width: 'toggle'}, function(e){
					clickedIcon.parent('li').removeClass('open-menu');
				});
			}else{
				$('.gb-icon-menu .open-menu .dropdown-list').animate({width: 'toggle'});
				$('.gb-icon-menu .open-menu').removeClass('open-menu');
				clickedIcon.parent('li').addClass('open-menu');
				clickedIcon.siblings('.dropdown-list').animate({width: 'toggle'});
			}
		});

		$('#hp-go-down > span').click(function(e){
			smoothScroll('#hp-content-outer', $('#gb-channel-list-outer').outerHeight() + 40);
		});

		initHeaderCurve();
		initFooterCurve();

		var scrollHookVal = $('#gb-channel-list-outer').position().top;
		scrollHookOn = scrollHookVal;
		scrollHookOff = scrollHookVal + 1;


		//$('#gb-sticky-channels-outer').append($(".gb-icon-menu.orig").clone(true, true).removeClass('orig'));

		$(window).scroll(function(e){ 
			stickyHeader(1024);
		});

		addFramesetTranslate();

		var customStyleSheet = (function() {
		    var style = document.createElement("style");

		    style.appendChild(document.createTextNode(""));

		    document.head.appendChild(style);

		    return style.sheet;
		})();

		var primaryColorRgb = hexToRgb('#185632');

		customStyleSheet.insertRule(".gb-icon-menu > #icons-container ul.dropdown-list > li .dropdown-list{ background: rgba(" + primaryColorRgb + ", 0.7); }", 0);
		customStyleSheet.insertRule(".gb-icon-menu > #icons-container ul.dropdown-list > li.open-menu{ background: rgba(" + primaryColorRgb + ", 0.7); }", 1);



		var socialMediaIcons = {
			".fb": ["Show"],
			".tw": ["Show"],
			".yt": ["Show"],
			".rss": ["Show"],
		};
		showHide(socialMediaIcons, "hide", false);

		$('.gb-global-to-top').click(function(e){
			smoothScroll('#gb-page', 0);
		});


		/* Added by Jeremy - Case #00112612 */
