			"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"buttonContainer"	: "body",
			"useDefaultCSS"		: "yes",
			"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
		});

	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			spSameHeights();
            hpSameHeights();
			var scrollHookVal = $('#gb-channel-list-outer').position().top;
			scrollHookOn = scrollHookVal;
			scrollHookOff = scrollHookVal + 1;

        	var gbHeaderOffset = $('#gb-header-outer').outerWidth() - (($(window).width() - 1280) / 2);
            if( ($(window).width() - 1280) < 0 ){
            	gbHeaderOffset = $('#gb-header-outer').outerWidth();
            }

        	$('#gb-channel-list-outer').css('top', $('#gb-custom-mystart-outer').outerHeight());
        	$('#gb-channel-list').css('padding-left', gbHeaderOffset + 18);

            $('#gb-custom-mystart').css('padding-left', gbHeaderOffset + 18);

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
            hpSameHeights();
			spSameHeights();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			spSameHeights();
			appView([".first-accordion"]);
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			multimediaRotator();
			appView([".first-accordion", ".second-accordion"]);
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView([".first-accordion", ".second-accordion"]);
		}

		if ( getWidth > 1023 ){ //only run on standard
			if( $('#gb-header .gb-icon-menu.orig').length <= 0 && $(".hp").length > 0){
				$('.gb-icon-menu.orig').removeClass('on-bottom').appendTo($('#gb-header'));
			}

            if( $('.sp').length > 0 ){
                var spPaddingTop = $('#gb-header-outer').outerHeight() - $('.sp-right-header').outerHeight();
                spPaddingTop = ( spPaddingTop <= 0 ) ? 30 : spPaddingTop += 30;
                $('#sp-content > .sp.column, #spn-content').css('padding-top', spPaddingTop);
        	}

            $('#gb-channel-list li.sw-channel-item').each(function(k, v){
                $('.sw-channel-dropdown', v).css('top', $(v).position().top + $(v).outerHeight());
            });
            $('#gb-sticky-channels li.sw-channel-item').each(function(k, v){
                $('.sw-channel-dropdown', v).css('top', $(v).position().top + $(v).outerHeight());
            });

			openMenuIcon = "icon-dbl-arrow-up";
			closedMenuIcon = "icon-dbl-arrow-down";

		}else{ //run on all other bps
			if( $('#gb-page > .gb-icon-menu.orig').length <= 0 && $(".hp").length > 0){
				$('.gb-icon-menu.orig').addClass('on-bottom').appendTo($("#gb-page"));
			}

			openMenuIcon = "icon-dbl-arrow-down";
			closedMenuIcon = "icon-dbl-arrow-up";
		}
		$('.gb-icon-menu .dropdown-trigger').removeClass(openMenuIcon + " " + closedMenuIcon).addClass(closedMenuIcon); //resets the icons when it goes from the top of the page to the bottom or vice versa
		moveHeader();
	}

	function spSameHeights(){
		if( $('#sp-content').length > 0 ){
			if( $('.sp.column.one').outerHeight() > $('.sp.column.two').outerHeight() ){
				$('.sp.column.two').css('min-height', $('.sp.column.one').outerHeight());
			}else{
				$('.sp.column.one').css('min-height', $('.sp.column.two').outerHeight());
			}	
		}
	}

    function hpSameHeights(){
    	if( $('.hp').length > 0 ){
        	var colH = 0;

            $('.hp.row.one .column > .region').css('min-height', 0);
            $('.hp.row.one .column').each(function(k, v){
            	if( $('> .region', v).outerHeight() > colH ) colH = $('> .region', v).outerHeight();
            });
            $('.hp.row.one .column > .region').css('min-height', colH);

        }
    }
