	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay("#sw-content-container2 .upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;

	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        }
	    }); 

        $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				//newDate += newDateTime[0] + "";
                $('.upcoming-column.right', this).prepend(newDateTime[0] + "");
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	
		});

		/* Moved out of dynamic content to here by Jeremy - Case #00111936 */
		/*if( $('.hp #sw-content-container2 .upcomingevents').length > 0 ){
			$('.hp #sw-content-container2 .upcomingevents ul.ui-articles > li').each(function(k, v){
				if(k > 4) $(this).remove();
			});
			$('.hp #sw-content-container2 .upcomingevents .view-calendar-link').text('View Full Calendar').appendTo($('.hp #sw-content-container2 .upcomingevents .ui-widget-header'));
		}*/
        $('.upcomingevents .view-calendar-link').text('VIEW ALL EVENTS');
	}

	function appView(toChange){
	    //DOC make homepage apps collapsible for small mobile device.

	    $.each(toChange, function(k, v){
	        //DOC dont add app into accordion if it doesnt have a header
	        $(v + " div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if($('.ui-widget-header h1 span.icon-font', this).length <= 0){
					$('.ui-widget-header > h1', this).append('');
				}
			}
	        });
	        //DOC accordion header click
	        $(v).off('click', 'div.ui-widget-header') //these needs to be here to remove click events when mobile devices scroll (which causes a screen resize event)
	            .on('click', 'div.ui-widget-header', function(){
		            if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
		                $(this).parent().removeClass("active");
		                $('span.icon-font', this).removeClass('icon-arrow-down').addClass('icon-arrow-right');
		            }else{
		                var clickedApp = $(this).parent().parent().attr("id");

		                $("#gb-page div.ui-widget.app.active span.icon-font").removeClass('icon-arrow-down').addClass('icon-arrow-right');
		                $("#gb-page div.ui-widget.app.active").removeClass("active"); 

		                $(this).parent().addClass("active");
		                $('span.icon-font', this).removeClass('icon-arrow-right').addClass('icon-arrow-down');
		                smoothScroll("#" + clickedApp, 3);
		            }
	        });
	    });
	}	

