	function clearGlobalIcons(parentId){
		var icoCont = $(parentId);
		$("> a", icoCont).each(function(k, v){
			if( $(this).attr('href') == "" ){
				$(this).remove();
			}else{
				$(this).attr('title', $(this).attr('alt'));
			}
		});

		if($("> a", icoCont).length <= 0) $(icoCont).parent('li').remove();
	}

