	/* showHide 
	** Use: To hide items based on a dynamic comparison, and if desired it will return you a formatted string to add into the mobile menu.
	** Params: 
		list -- this is an obj {} of items that you want to hide, or leave showing.
			*EXAMPLE*  { #id: [url *used for comparison*, [menuOptionNumber, label, target]] }
		comp -- is the value you want it to compare against to determine if it should be hidden or not.
		menuOption -- is a bool to tell the function whether or not you want a menu option string returned to you
	*/
	function showHide(list, comp, menuOption){
		var menuOptionString = "";

		$.each(list, function(k, v){
			if((v[0].toLowerCase() === comp) || comp === true){
				$(k).hide();
			}else if(menuOption){
					//build out the menu option string
				menuOptionString += v[1][0] + "," + v[1][1] + "," + v[0] + "," + v[1][2] + ";";
			}
		});

		return menuOptionString;
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};

	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	function stickyHeader(shutoff){
		var getWidth = $(window).width();
		if( getWidth >= shutoff ){
			var top = $(window).scrollTop();
				//check where the window is
			if(top >= scrollHookOn  && !$('#gb-sticky-channels-outer').hasClass('on')){ //make it sticky
				$('#gb-sticky-channels-outer').addClass('on');
			}else if( top <= scrollHookOff  && $('#gb-sticky-channels-outer').hasClass('on')){ //unstick it
				$('#gb-sticky-channels-outer').removeClass('on');
			}
		}else if( $('#gb-sticky-channels-outer').hasClass('on') ){
			$('#gb-sticky-channels-outer').removeClass('on');
		}
	}

    var lastOneLoaded = false;
    var channelLoadWaiter = "";
    var safetyCount = 0;
    var loadedUp = 0;
    var channelItemsSetToLoad = 0;

    function getChannelDirectories() {
    	var lastChannelItem = false;
        $("#gb-channel-list .sw-channel-item").each(function(k, v) {
        	if(k == ($("#gb-channel-list .sw-channel-item").length - 1)) lastChannelItem = true;

			if($("a span", this).text() != "Home" && $("a span", this).text() != "Calendar" && !$(".sw-channel-dropdown", this).length) {

                channelItemsSetToLoad++;

                $(this).append('
');
                $(".sw-channel-dropdown", this).load($("a", this).attr("href") + " #sw-content-layout-wrapper", 
                	function(e){

                    	var channelDropdown = $(this).parent();

                        //Organize data
                        var newList = "";
                        if( $('.sw-channel-dropdown .sw-directory-columns', channelDropdown).length > 0 ){
                                //once we have the page loaded in loop through each column of the directory app
                            $('.sw-channel-dropdown .sw-directory-columns > div', channelDropdown).each(function(k1, v1){ 
                                //Go through each UL within that column (the system has an odd structure for this app)
                                $("> ul > *", v1).each(function(k2, v2){
                                    if( $(v2).hasClass('sw-directory-alphabetical-group-heading') ){ //if we hit a new heading make sure to stop our list and add in the new heading
                                        if(newList == ""){	//if we are on the first one then we don't need to close a , but we will need to open the first one
                                            newList += "

" + $(v2).text() + "";
                                        }else{	//on every other header we will close the UL AND start a new one.
                                            newList += "

" + $(v2).text() + "";
                                        }
                                    }else{
                                        newList += $(v2).html();
                                    }
                                });
                            });

                            newList += "";
                            $(".sw-channel-dropdown", channelDropdown).html(newList);



                            //Break data up
                            var splitOn = 9; //there is a section max of 9

                            if( $('.directory-section', channelDropdown).length%3 == 0){
                                //each column gets equal sections
                                splitOn = $('.directory-section', channelDropdown).length/3;
                            }else{ //we have a remainder
                                    //by doing this we ensure all overflow will go into the first or first and second columns
                                splitOn = Math.ceil($('.directory-section', channelDropdown).length/3);
                            }

                            var colCount = 2;
                            var colHtml = '
';
                            $('.directory-section', channelDropdown).each(function(k, v){
                                if( k%splitOn == 0 && k != 0 ){ //open a new column
                                    colHtml += '
';
                                    colCount++;
                                }
                                colHtml += '
' + $(v).html() + '';
                            });
                            colHtml += '';
                            $(".sw-channel-dropdown", channelDropdown).addClass('mega-channel').html(colHtml);

                        }else{
                        	$(this).remove();
                            $(v).addClass('no-dropdown')
                        }

                        loadedUp++;
                        if( lastChannelItem == true && loadedUp == channelItemsSetToLoad){
							lastOneLoaded = true;
                        }
            		});
            }else{
            	$(v).addClass('no-dropdown has-channel-list');
            }
        });

        channelLoadWaiter = setInterval(function(e){
        	if(lastOneLoaded){
            	$('#gb-sticky-channels').append($('#channel-navigation').clone(true));
                $('#gb-sticky-channels .sw-channel-item').each(function(k, v){
                	$(this).attr('id', $(this).attr('id') + '-sticky');
                });

                $("#gb-channel-list .mega-channel a").keydown(function(e) {
                    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                    var linkObj = $('#gb-channel-list .sw-channel-item.hover a');
                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case csKeyCodes.left:
                        case csKeyCodes.up:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS FIRST ITEM
                            if(linkObj.index($(this)) == 0) {
                                $('.sw-channel-item.hover > a').focus();
                            } else {
                            	linkObj[linkObj.index($(this)) - 1].focus();
                                //$(this).parent().prev("li").find("> a").focus();
                            }
                        break;

                        // CONSUME RIGHT AND DOWN ARROWS
                        case csKeyCodes.right:
                        case csKeyCodes.down:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS LAST ITEM
                            if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                                // FOCUS FIRST ITEM
                                linkObj[0].focus();
                            } else {
                                // FOCUS NEXT ITEM
                                linkObj[linkObj.index($(this)) + 1].focus();
                            }
                        break;
                    }
                });

            	clearInterval(channelLoadWaiter);
                safetyCount++;
                if( safetyCount == 100 ){
                	clearInterval(channelLoadWaiter);
               	}
            }
        }, 300);
    }

