        "StreamingVideo": function() {
            // FOR SCOPE
            var _this = this;

	        if($("#player").length) {
                // VIMEO API - https://developer.vimeo.com/player/js-api | https://player.vimeo.com/playground
                // CUSTOMIZE EMBED - https://vimeo.com/blog/post/customize-the-embedded-vimeo-player
                var iframe = $("#vimeoBgVideo")[0];
                var player = $f(iframe);
                var videoStarted = false;

                // VIMEO PLAYER IS READY
                player.addEvent("ready", function() {
                    // MUTE THE VIDEO
                    player.api("setVolume", 0);

                    // LOOP THE VIDEO
                    if(!$(".ie9").length) {
                        player.api("setLoop", true);
                    }

                    // SHOW THE PLAY/PAUSE BUTTON WHEN VIDEO STARTS
                    if(!videoStarted) {
                        videoStarted = true;
                    }

                    // BIND VIDEO EVENTS
                    $(document).on("click keydown", "#video-button", function(e) { 
                        if(_this.A11yClick(e)) {
                            // PAUSE
                            if($(this).hasClass("paused")) {
                                player.api("play");
                                $(this).attr("aria-label", "Pause Video").removeClass("paused").addClass("playing");
                            }
                            // PLAY
                            else {
                                player.api("pause");
                                $(this).attr("aria-label", "Play Video").removeClass("playing").addClass("paused");
                            }
                        } 
                    });
                });

                _this.CSVideoResize();
            }
        },

