        "Search": function() {
            // FOR SCOPE
            var _this = this;

            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
                window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
            });

            $(document).on("click keypress", ".gb-search-btn", function(e) { 
                if(_this.A11yClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$("nav").addClass("cs-hide-active");

                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-form").attr("aria-hidden", "false").fadeIn(250);

                        setTimeout(function() {
                            $("#gb-search-input").addClass().focus();
                        }, 260);

                    } else {
                    	$("nav").removeClass("cs-hide-active");

                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").fadeOut(250);
                    }
                }
            });
        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "A11yClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "CSVideoResize": function () {
            if($("#player").length && false === true) {
                var getWidth = $(window).width();
                if (getWidth >= 1024) { // 960+
                    var topOffSet = $("#hp-video").offset().top;
                    var screenWidth = $(window).width();
                    var screenHeight = $(window).height() - 143 - topOffSet;
                    var screenRatio = screenHeight / screenWidth;
                    var containerHeight = screenWidth * screenRatio;
                    var vidWidth = screenWidth, vidHeight = 0;

                    // GET IMAGE RATIO
                    var initW = 853.5, initH = 480;
                    var ratio = initH / initW;
                    vidHeight = vidWidth * ratio;
                    if(vidHeight < screenHeight){
                        vidHeight = screenHeight;
                        vidWidth = vidHeight / ratio;
                    }
                    var centerWidth = (vidWidth - screenWidth) / 2;
                    var centerHeight = (vidHeight - screenHeight) / 2;

                    // APPLY THE DIMENSIONS
                    $("#hp-video").css({"height": containerHeight + "px"});
                    $("#vimeoBgVideo").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
                } if (getWidth >= 480 && getWidth < 1024) { // 768 - 480
                    // GET IMAGE RATIO
                    var initW = 853.5, initH = 480;
                    var ratio = initH / initW;
                    var vidWidth = $("#hp-video").width();
                    vidHeight = vidWidth * ratio;

                    // APPLY THE DIMENSIONS
                    $("#hp-video").css({"height": vidHeight + "px"});
                    $("#vimeoBgVideo").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
                } if (getWidth < 480) { // 320
                    // GET IMAGE RATIO
                    var initW = 853.5, initH = 480;
                    var ratio = initH / initW;
                    var vidWidth = $("#hp-video").width();
                    vidHeight = vidWidth * ratio;

                    // APPLY THE DIMENSIONS
                    $("#hp-video").css({"height": vidHeight + "px"});
                    $("#vimeoBgVideo").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
                }
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

