function runScrollbars(){
  	var element = $('.announcements-inner').jScrollPane({});
  	var api = element.data('jsp');
  	api.destroy();

  	var element = $('.events-inner').jScrollPane({});
  	var api = element.data('jsp');
  	api.destroy();
	
	var element = $('.welcome-content--inner').jScrollPane({});
  	var api = element.data('jsp');
  	api.destroy();

 	getScrollbars('.announcements-inner');
 	getScrollbars('.events-inner');
	getScrollbars('.welcome-content--inner');
}

