	function switchDip_287282(num,pause){
		clearTimeout(slideshowTimer_287282);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_287282();
		var speed = slideshowMotionSpeed_287282();
		var currentNum = document.getElementById('currentNum_287282').value;
		var totalDips = document.getElementById('totalDips_287282').value;
		var video = document.getElementById('video_287282').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_287282(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_287282' + nums).length){
				$('#nav_287282' + nums).removeClass('on');
			}
			
			if($('#nav_li_287282' + nums).length){
				$('#nav_li_287282' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_287282' + nums).length){
					$('#videoShell_287282' + nums).hide();
				}
				$('#dipImage_287282' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_287282' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_287282' + nums).length){
				$('#videoShell_287282' + nums).fadeOut(speed);
			}
			if($('#dipContent_287282' + nums).length){
				$('#dipContent_287282' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_287282' + nums).length){
				$('#nav_287282' + nums).addClass('on');
			}
			
			if($('#nav_li_287282' + nums).length){
				$('#nav_li_287282' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_287282' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_287282' + nums).length){
					$('#videoShell_287282' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_287282' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_287282' + nums).length){
					$('#videoShell_287282' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_287282' + nums).length){
				$('#dipContent_287282' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_287282(timer);
			document.getElementById('currentNum_287282').value = nums;
		}
	}
	
	function slideshowMotion_287282(){
		if($('#slideshowMotion_287282').length){
			slideshowMotion = document.getElementById('slideshowMotion_287282').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_287282(){
