			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_287282').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_287282(){
		if($('#slideshowQuickNavSpeed_287282').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_287282').value;
		}else{
