				$('#videoShell_362887' + nums).fadeOut(speed);
			}
			if($('#dipContent_362887' + nums).length){
				$('#dipContent_362887' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_362887' + nums).length){
				$('#nav_362887' + nums).addClass('on');
			}
			
			if($('#nav_li_362887' + nums).length){
				$('#nav_li_362887' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_362887' + nums).show("slide", { direction: directionOn }, speed, function(){
