    $(window).resize(function() {
    	if($(window).width() > 1023) {
          var setSlideshowHeight = $(window).height() - 100;
          $("#hp-slideshow .rs-photo-gallery-container, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images ul, #hp-slideshow .rs-photo-gallery-images ul li").css({
            "height": setSlideshowHeight
          });

          // SET TOP POSITION ON PLAY PAUSE BUTTON AND SLIDESHOW DESC
          var getChannelBarHeight = $("#gb-channel-list-outer").outerHeight(true);
          var playPauseTop = getChannelBarHeight + 20;
          $("#hp-slideshow .rs-photo-gallery-control.play-pause").css({
          	"top": playPauseTop + "px"
          });
          var slideshowDescTop = playPauseTop + 45;
          $("#hp-slideshow .rs-photo-gallery-desc").css({
          	"top": slideshowDescTop + "px"
          });
        } else {
          $("#hp-slideshow .rs-photo-gallery-desc").css({
            "top": "auto"
          });
          $("#hp-slideshow .rs-photo-gallery-control.play-pause").css({
          	"top": "70px"
          });
        }
    });

    function headlinesAppScroller() {
    	if(csWindowWidth >= 1280) {
          $("#sw-content-container3.region.ui-hp div.ui-widget.app.headlines").appScroller({
              "appParent"             : "#hp-content-bottom",
              "targetApp"             : "headlines", //upcomingevents or headlines
              "imageNum960"           : 4, //NUMBER OF IMAGES TO DISPLAY AT 960
              "dynamicWidth"          : "no",
              "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
              "extraBackIconClass"    : "insight-icon-font border-box",
              "extraNextIconClass"    : "insight-icon-font border-box",
              "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
          });
          if($("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").length <=  4) {
              $("#hp-content-bottom .headlines-scroll-ctrl").hide();
          }
      	} else {
        	$("#sw-content-container3.region.ui-hp div.ui-widget.app.headlines").appScroller({
              "appParent"             : "#hp-content-bottom",
              "targetApp"             : "headlines", //upcomingevents or headlines
              "dynamicWidth"          : "yes",
              "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
              "extraBackIconClass"    : "insight-icon-font border-box",
              "extraNextIconClass"    : "insight-icon-font border-box",
              "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
        });
      }

      $("#hp-content-bottom .ui-widget.app.headlines").each(function() {
          $(".ui-article", this).each(function() {
              if(!$(".cs-article-content-cont",this).length) {
                  $("
").prependTo(this);
              }

              var headlineURL = $("h1 a", this).attr("href");
              if(!$(".custom-teaser-read-more",this).length) {
              	$(".cs-article-content-cont",this).append('î¤‡');
              }

              var headlineDesc = $("p",this).text();
              if(!$(".cs-headline-desc",this).length) {
              	$(".cs-article-content-cont",this).prepend("
" + headlineDesc + "");
              }

              var headlineTitle = $("h1 a span",this).text();
              if(!$(".cs-headline-title",this).length) {
              	$(".cs-article-content-cont",this).prepend("
" + headlineTitle + "");
              }

              var contHeight = $(".cs-article-content-cont",this).height();
              var titleHeight = $(".cs-headline-title",this).height();
              $(".cs-article-content-cont",this).css({
                  "bottom": "-" + contHeight + "px"
              });

              var newBottom = (contHeight - titleHeight) - 50;
              $(this).hover(function() {
                  var $this = this;
                  $(".cs-article-content-cont",this).css({
                      "bottom": "-" + newBottom + "px"
                  });
              }, function() {
                  $(".cs-article-content-cont",this).css({
                      "bottom": "-" + contHeight + "px"
                  });
              });

              $(".cs-article-content-cont",this).hover(function() {
                  $(this).css({
                      "bottom": "0px"
                  });
                  $("#hp-content-bottom .custom-teaser-read-more span").css({
                      "top": "50%",
                      "margin-top": "-17px"
                  });
              }, function() {
                  $(this).css({
                      "bottom": "-" + newBottom + "px"
                  });
                  $("#hp-content-bottom .custom-teaser-read-more span").css({
                      "top": "10px",
                      "margin-top": "0"
                  });
              });
          });
      });
    }

	$(function(){
    	//STICKY HEADER
        var docElem = $(document),
        	channelsHeight = $("#gb-channel-list-outer").height(),
            outerHeaderHeight = $("#gb-header-outer").height(),
        	lastScrollTop = 0;

        $(docElem).scroll(function() {
        	var currentScrollTop = $(this).scrollTop();

            if($(window).width() >= 1024) {
            if(currentScrollTop > channelsHeight + outerHeaderHeight) {
              if(currentScrollTop > lastScrollTop) {
                  $("#gb-channel-list-outer").slideUp();
              } else {
                if(!$("#gb-channel-list-outer.sticky-channels").length) {
                    $("#gb-channel-list-outer").addClass("sticky-channels").prependTo("#gb-page").hide();
                }
                $("#gb-channel-list-outer.sticky-channels").slideDown();

                if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                    }
                }

                if(currentScrollTop < 100) {
                    if(!$("#hp-slideshow-outer #gb-channel-list-outer").length) {
                      $("#gb-search-container").after($("#gb-channel-list-outer"));
                    }
                    $("#gb-channel-list-outer").removeClass("sticky-channels").show();
                }
              }

            } else {
            	if(!$("#hp-slideshow-outer #gb-channel-list-outer").length) {
                  $("#gb-search-container").after($("#gb-channel-list-outer"));
                }
                $("#gb-channel-list-outer").removeClass("sticky-channels");

                if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                    }
                }
            }
            lastScrollTop = currentScrollTop;
            }
