    function headerElements() {
    	var useLogo = 'true';
        if(useLogo == 'true') {
          var getLogoWidth = $("#gb-logo img").width();
          $("#gb-logo").width(getLogoWidth);
          $("ul.sw-channel-list").css("margin-left",getLogoWidth + 47);
          $("#gb-sitename-outer").css("margin-left",getLogoWidth + 17);
        }

        var headerHeight = $("#gb-header-outer").height();
        var mystartHeight = $("#sw-mystart-outer").height();
        var setSitenameHeight = headerHeight - mystartHeight;
        $("#gb-sitename").height(setSitenameHeight - 30);
    }

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
        	setGalWidth = $(window).width() - 100;
			//photoGallery();
            //var windowHeight = $(window).height();
            //$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height(windowHeight);
            //APP SCROLLER
            if($(".hp #hp-content-bottom .headlines").length) {
                headlinesAppScroller();
            }
			//logoWidth();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
            //photoGallery();
            //var calcHeight = $("#hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height();
            //$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height(calcHeight);
			//logoWidth();
            //APP SCROLLER
            if($(".hp #hp-content-bottom .headlines").length) {
                headlinesAppScroller();
            }
            appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
            //photoGallery();
            //var calcHeight = $("#hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height();
            //$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height(calcHeight);
			//logoWidth();
            //APP SCROLLER
            if($(".hp #hp-content-bottom .headlines").length) {
                headlinesAppScroller();
            }
            appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
            //photoGallery();
            //var calcHeight = $("#hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height();
            //$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height(calcHeight);
			appView();
			//logoWidth480();
            //APP SCROLLER
            if($(".hp #hp-content-bottom .headlines").length) {
                headlinesAppScroller();
            }
		} if (getWidth >= 320 && getWidth < 480) { // 320+
            //photoGallery();
            //var calcHeight = $("#hp-slideshow .rs-photo-gallery-images .rs-photo-gallery-images-list li").height();
