		}
	}

    function photoGallery() {
        if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
        	// PHOTO GALLERY PLACEHOLDER
            if(!$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                $("#sw-content-container10.ui-hp").html("


var photoGallery0000={'Album':{'title': 'Photo Gallery','description': '','transitiondelay': '6','fadestyle': 'NO','gallerywidth': '1500','backgroundaudio': '','loopaudio': 'False','lgpath': '','tnpath': '','Images': [{'photoname': 'Photography Showcase','src': '/cms/lib/CA02218491/Centricity/Template/17/defaults/slideshow3.jpg','caption': '','pause': '','link': '','target': ''}]}}</" + "scr" + "ipt>");
            }

            var showPlayPause = 'false';
            if(showPlayPause == 'true') {
                showPlayPause = 'yes';
            } else {
                showPlayPause = 'no';
            }

            var slideshowWidth = $("#hp-slideshow").width();
            var slideshowHeight = $(window).height() - 100;
            if($(window).width() > 1023) {
            	slideshowWidth = slideshowWidth;
                slideshowHeight = slideshowHeight;
            } else {
            	slideshowWidth = 1500;
                slideshowHeight= 915;
            }
            $(window).resize(function() {
                if($(window).width() > 1023) {
                    slideshowWidth = slideshowWidth;
                    slideshowHeight = slideshowHeight;
                } else {
                    slideshowWidth = 1500;
                    slideshowHeight= 915;
                }
            });
            var windowHeight = $(window).height() - 100;
            // PHOTO GALLERY ROTATOR
            $("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
                    "imageWidth"                : slideshowWidth,
                    "imageHeight"               : slideshowHeight,
                    "efficientLoad"             : "yes",
                    "appHeader"                 : "no",
                    "titles"                    : "yes",
                    "captions"                  : "yes",
                    "playPause"                 : showPlayPause,
                    "mainControls"              : "yes",
                    "galleryOverlay"            : "no",
                    "linkedElement"             : ["all"], // ["image", "title", "overlay", "all"]
                    "contMoreLink"              : "no",
                    "contMoreLinkText"          : "Read More",
                    "descMoreLink"              : "no",
                    "descMoreLinkText"          : "READ MORE",
                    "bullets"                   : "no",
                    "bulletControls"            : "no",
                    "thumbnails"                : "no",
                    "thumbnailWidth"            : 122,
                    "thumbnailHeight"           : 53,
                    "thumbnailSlideDirection"   : "horizontal", // horizontal or vertical
                    "hoverPause"                : "yes",
                    "fullScreenRotator"         : "no", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints"     : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "autoRotate"                : "yes",
                    "transitionType"            : "slide", // fade or slide
                    "transitionSpeed"           : 2,
                    "onImageLoad"               : function() { },
                    "allImagesLoaded"           : function() { },
                    "onThumbnailLoad"           : function() { },
                    "allThumbnailsLoaded"       : function() { },
                    "onTransitionStart"         : function() {
                        //CHECK IF PHOTO HAS A LINK
                        $(".rs-photo-title").each(function() {
                          var getDesc = $(this).parent();
                          if($(".rs-photo-title a").length) {
                            $(getDesc).addClass("has-link");	
                          } else {
                              $(getDesc).removeClass("has-link");
                          }
                        });
                    },
                    "onTransitionEnd"           : function() { },
                    "allLoaded"                 : function(element, galleryWidth, galleryHeight) {
                        if($(".rs-photo-gallery-desc").length) {
                            $(".rs-photo-gallery-desc").wrapInner("
");
                        }

                        //CHECK IF PHOTO HAS A LINK
                        $(".rs-photo-title").each(function() {
                          var getDesc = $(this).parent();
                          if($(".rs-photo-title a").length) {
                            $(getDesc).addClass("has-link");	
                          } else {
                              $(getDesc).removeClass("has-link");
                          }
                        });

                        $(".rs-photo-gallery-control.play-pause").prependTo(".rs-photo-gallery-container");

                        if(!$("#gb-global-icons-outer .global-icon").length) {
                        	$("#hp-slideshow-outer .scroll-more-slideshow").addClass("no-icons");	
                        } else {
                        	var getIconsHeight = $("#gb-global-icons-outer").height();
                            getIconsHeight = getIconsHeight + 15;
                            $(".scroll-more-slideshow").css("bottom",getIconsHeight + "px");
                        }
                    }
           });

        }
    }

	function logoWidth() {
		if($("#gb-logo img").length) {
			var imgWidth = $("#gb-logo img").width();
			$("#gb-logo").width(imgWidth);
		}
	}

	function logoWidth480() {
    	var getLogoWidth = $("#gb-logo img").width();
		if($("#gb-logo img").length) {
			var imgWidth = getLogoWidth*.75;
			$("#gb-logo, #gb-logo img").width(imgWidth);
		}
	}

	function appView(){
    	if(!$("#hp-content-top div.ui-widget.app.active").length) {
        	$("#hp-content-top div.ui-widget.app").first().addClass("active");
        }

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-top div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
        $('#hp-content-top .ui-widget-header')
        	.off('click') //these needs to be here to remove click events when mobile devices scroll (which causes a screen resize event)
            .click(function(){
              if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
                  $(this).parent().removeClass("active");
              }else{
                  var clickedApp = $(this).parent().parent().parent().attr("id");
                  $(this).parent().addClass("active");
                  smoothScroll("#" + clickedApp, 3);
              }
       		});
	}	

    function smoothScroll( to, offset ){
        if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
        var target = $(to);

        if (target.length) {
            $('html,body').animate({
                scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
            }, 800, function(){
                $("html, body").stop(true, false);
            });
            return false;
        }
    }

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

