$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=E5%2bfDsNDwvzvyhcQ5YB6KA%3d%3d&SessionID=76f4e594-9c77-4ecc-a5cd-757f0859d07c&SiteID=21&ChannelID=0&SectionID=21&PageID=35&HitDate=4%2f29%2f2020+8%3a58%3a37+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.80.193';
