$( document ).ready(function() {
//$('.sw-mystart-nav.sw-mystart-button.home a span').html('REDCLAYSCHOOLS.COM');
//$('.sw-mystart-button.home a').attr('href', 'http://www.redclayschools.com');
$('.RedClay.noble-fusion .gb.footer.one .footer-inner p strong').text('Red Clay Consolidated School District');

    //Beginning of case added by Lyndsay - Case 00351801
    var csMobileSchoolsAdded = false;
    $.schoolListMinibase({
        'miniBaseDomainID': 4,
        'miniBaseModuleInstanceID': 1693,
        'cache': true,
        'schoolListReady': function(schoolList) {
            /*
            The returned value is an array of objects and each object is a school record.
            The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
            */

            var schools = '';

            if('RedClay' == 'InsideRedClay') {
                // SCHOOL LIST CODE FOR NOBLE FUSION
                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);

                // ADD SCHOOLS TO MOBILE
                checkMobileSchools(schools);

            } else {
                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }
                var schoollist = $('.sw-mystart-dropdown.schoollist').html();// added by Matt B - case#3995617 - 10.23.18 
				$('.gb-custom-dropdown.schoollist').html(schoollist).attr('tabindex', '0');// added by Matt B - case#3995617 - 10.23.18
                $('.gb-custom-dropdown.schoollist .sw-dropdown-list').html(schools);

                // ADD SCHOOLS TO MOBILE
                checkMobileSchools(schools);
            }
        }
    });

    function checkMobileSchools(schools) {
        // ADD SCHOOLS TO MOBILE
        $(window).load(function(){
            if(!csMobileSchoolsAdded) {
                if($('#rs-menu .rs-menu-option.schools').length) {
                    $('.rs-menu-option.schools > .rs-menu-option-sublist li').not($('.rs-menu-option-sublist-header')).not($('.rs-menu-option-sublist-name')).remove();
                    $('.rs-menu-option.schools > .rs-menu-option-sublist').append(schools);
                    csMobileSchoolsAdded = true;
                }
            }
        });
        $(window).resize(function(){
            if(!csMobileSchoolsAdded) {
                if($('#rs-menu .rs-menu-option.schools').length) {
                    $('.rs-menu-option.schools > .rs-menu-option-sublist li').not($('.rs-menu-option-sublist-header')).not($('.rs-menu-option-sublist-name')).remove();
                    $('.rs-menu-option.schools > .rs-menu-option-sublist').append(schools);
                    csMobileSchoolsAdded = true;
                }
            }
        });
    }
});

