      $('.hp.row.two .global-icon').height('auto');//-- Resets Height To get Tallest on resize
      var hG = '',
          size = $('.hp.row.one .headlines .ui-article').size();
      $('.hp.row.one .headlines .ui-article').each(function(k, v){
          var h = $(v).height();
          if(h > hG){
              hG = h;
          }
          if(size == (k + 1)){
