    }

    function onLoads(){


        //-- Check White Grey Toggle
        var sC = 'White';
        if(sC != 'White;Grey' && sC != 'White'){
        	$('.hp-overlay-header h2, .hp-overlay-learnmore, #gb-footer-social h1').css('color', '#333333');
            $('.hp-overlay-learnmore .arrow .cls-1, #gb-footer-social svg .cls-1').css({
            	fill : '#333333',
                stroke : '#333333'
            });
            $('.hp.row.one .ui-widget.app.headlines .custom-teaser-read-more').each(function(){
            	$(this).css('color', '#333333');
            });
        }

        //-- Remove Upcoming Event Listings Over 7
        $('.hp.row.three .upcomingevents .ui-articles > li').each(function(k ,v){
        	if(k >= 7){
            	$(v).remove();
            }
            if(k == 0){
            	$('.left', v).addClass('first').addClass('open');
                $('.right', v).addClass('open');
            }
            if(k == 6){
            	$('.left', v).addClass('last');
            }

            $('.right p:first-child a').addClass('first');
            $('.right p:last-child a').addClass('last');

            $('.gb-block').height($('#gb-header-bottom').height());

        });

        $('.navigation .bullet.expandable, .navigation .bullet.collapsible').append(bullet);



        //$('.myaccount .sw-dropdown-list').prepend($('#sw-myaccount-list li:first-child'));
        if($('.hp').size() > 0){
        	if($('.hp .hp-overlay-learnmore').length) {
              if($('.hp .hp-overlay-learnmore').attr('href').length == 0 ){
                  $('.hp-overlay-learnmore').remove();
              }
            }  
        }

        if($('.rs-title').hasClass('hidden') && $('.rs-caption').hasClass('hidden')){
            $('.btn.hp-desc-btn').hide();
        } else {
            $('.btn.hp-desc-btn').show();
        }
        setTimeout(function(){
            var accountList = $('ul#sw-myaccount-list').html();

            $('#gb-user-options').append($('#ui-btn-signin').html()).append($('#ui-btn-register').html());


            $('#gb-sticky-header').html($('#gb-header-bottom').html());

              var wScroll = $(window).scrollTop();
              if(wScroll > 30){
                  $('#gb-sticky-header').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom .gb-logo').hide().attr('aria-hidden', 'true');
                  $('#gb-header-cover').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom').addClass('scrolling');
              } else {
                  $('#gb-sticky-header').hide().attr('aria-hidden', 'true');
                  $('#gb-header-bottom .gb-logo').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom').removeClass('scrolling');
                  $('#gb-header-cover').hide().attr('aria-hidden', 'true');	
              }

              $('#gb-sticky-header .gb-custom-dropdown.myaccount').hover(function(){
                  var dropdown = $('.sw-dropdown', this);
                  if(!(dropdown.hasClass('open'))){
                    dropdown.slideDown().attr('aria-hidden', 'false').addClass('focus').addClass('open');
                    $('.sw-dropdown li a', this).attr('tabindex', '-1');
                  }
              },function(){//-- Mouse Leave
                  var dropdown = $('.sw-dropdown', this);
                  if(dropdown.hasClass('open')) {
                      dropdown.stop().slideUp().attr('aria-hidden', 'true').removeClass('focus').removeClass('open');
                      $('.sw-dropdown li a', this).attr('tabindex', '-1');
                  }
              }).focus(function(){ //-- On Focus 
                  var dropdown = $('.sw-dropdown', this);
                  if(!(dropdown.hasClass('open'))){
                    dropdown.stop().slideDown().attr('aria-hidden', 'false').addClass('focus').addClass('open');
                    $('.sw-dropdown li a', this).attr('tabindex', '-1');
                  }
              }).keydown(function(e){ //-- Go into Dropdown
                // Trigger the click event from the keyboard 
                  var code = e.which;
                  var dropdown = $('.sw-dropdown', this);
                  if (code == kDown) {
                      e.preventDefault();
                      $('.sw-dropdown-list li:first-child a', this).addClass('first');
                      if(!(dropdown.hasClass('inner'))){
                          dropdown.addClass('inner');
                          $('.sw-dropdown li:first-child a', this).focus();
                          $('.sw-dropdown li a', this).attr('tabindex', '0');
                      }
                  }
              }).blur(function(){ //-- Focus Leaves
                  var dropdown = $('.sw-dropdown', this);
                  if(!(dropdown.hasClass('inner'))) {
                      dropdown.stop().slideUp().attr('aria-hidden', 'true').removeClass('focus').removeClass('open');
                      $('.sw-dropdown li a', this).attr('tabindex', '-1');
                  }

              });//-- End Keydown
        }, 500);
    }

    function resizing() {

        if($(window).width() >= 1024){
          $(window).unbind('scroll').on('scroll', function(){
              var wScroll = $(window).scrollTop();
              if(wScroll > 30){
                  $('#gb-sticky-header').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom .gb-logo').hide().attr('aria-hidden', 'true');
                  $('#gb-header-cover').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom').addClass('scrolling');
              } else {
                  $('#gb-sticky-header').hide().attr('aria-hidden', 'true');
                  $('#gb-header-bottom .gb-logo').show().attr('aria-hidden', 'false');
                  $('#gb-header-bottom').removeClass('scrolling');
                  $('#gb-header-cover').hide().attr('aria-hidden', 'true');	
              }
          });//-- End Scroll
        } else {
          $(window).unbind('scroll').on('scroll', function(){
              var wScroll = $(window).scrollTop();
              if(wScroll > 30){
                  $('#gb-sticky-header').hide();
              } else {
                  $('#gb-sticky-header').hide().attr('aria-hidden', 'true');	
              }
          });//-- End Scroll
        }

    	var vpw = $(window).width();
        if(vpw < 1280){
        	$('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
                if(k == 5){
                    $(v).show().addClass('last').attr('aria-hidden', 'false');
                    $('.ui-article', v).addClass('last');
                } else if(k >= 6){
                    $(v).hide().removeClass('last').attr('aria-hidden', 'true');
                    $('a', v).attr('tabindex', '-1');
                }
            });//-- Down to 5 events
            if(vpw < 1024){
                $('#gb-header-right-lower').prepend($('#gb-header-bottom .gb-sitename.two'));
                $('.gb-header-top-left').prepend($('#gb-header-bottom  .gb-header-social'));
                $('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
                    if(k == 4){
                        $(v).show().addClass('last').attr('aria-hidden', 'false');
                        $('.ui-article', v).addClass('last');
                    } else if(k >= 5){
                        $(v).hide().removeClass('last').attr('aria-hidden', 'true');
                        $('a', v).attr('tabindex', '-1');
                    }
                });//-- Down to 5 events
                $('#hp-content-outer').prepend($('#hp-slideshow-left-container'));
                if(vpw < 768){
                    $('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
                        if(k == 3){
                            $(v).show().addClass('last').attr('aria-hidden', 'false');
                            $('a', v).attr('tabindex', '0');
                        } else if(k == 4) {
                            $(v).hide().removeClass('last').attr('aria-hidden', 'true');
                            $('a', v).attr('tabindex', '-1');
                        }
                    });//-- Down to 4 events

                    //-- Calendar Month View
                    $("a.ui-btn-toggle.month").click();

                    if(vpw < 640){
                        $('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
                            if(k == 2){
                                    $(v).show().addClass('last').attr('aria-hidden', 'false');
                                    $('a', v).attr('tabindex', '0');
                            } else if(k == 3){
                                $(v).hide().removeClass('last').attr('aria-hidden', 'true');
                                $('a', v).attr('tabindex', '-1');
                            }
                        });//-- Down to 3 events

                        //-- Calendar list view
                        $("a.ui-btn-toggle.list").click();

                        if(vpw < 480){
                            $('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
                                if(k == 2){
                                    $(v).hide().removeClass('last').attr('aria-hidden', 'true');
                                    $('a', v).attr('tabindex', '-1');
                                }
                            });//-- Down to 2 events
                        }
                    }
                }
            }
        } else if(vpw >= 1024) {
        	$('.hp.row.three .ui-widget.app.upcomingevents .ui-articles li').each(function(k, v){
            	if(k == 4){
                    $(v).removeClass('last').attr('aria-hidden', 'false');
                } else if(k >= 5 && k <= 6){
                	$(v).show().attr('aria-hidden', 'false');
                    $('a', v).attr('tabindex', '0');
                    if(k == 6){
                    	$(v).addClass('last');
                    }
                }
            });//-- Put events Back
            $('#hp-overlay-placeholder').append($('#hp-slideshow-left-container'));
            $('#gb-header-bottom .gb-sitename-outer').append($('#gb-header-bottom .gb-sitename.two')).append($('.gb-header-top-left .gb-header-social'));

        }
        if($('#rs-menu-btn svg').size() < 1){
        	$('#rs-menu-btn').append(menu);
        }
        if($('#rs-pagelist-menu-btn svg').size() < 1){
        	$('#rs-pagelist-menu-btn').append(menu);
        }

        //-- Resize Global Icons to match in Height

        $('.hp.row.two .global-icon').height('auto');//-- Resets Height To get Tallest on resize
        var tG = '',
            size = $('.hp.row.two .global-icon').size();
        $('.hp.row.two .global-icon').each(function(k, v){
            var h = $(v).height();
            if(h > tG){
                tG = h;
            }
            if(size == (k + 1)){
                $('.hp.row.two .global-icon').height(tG);
            }
        });//-- End Each
    }

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				appViewReset();
			break;
			case "768":
				appViewReset();
			break;
			case "640":
				appViewReset();
			break;
			case "480":
				appViewReset();
			break;
			case "320":
				appViewReset();
			break;
		}
	}

