		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() { 

                    	$('.rs-gallery-control').append(arrow);
                        $('.rs-gallery-desc').wrap('
');
                        $('.rs-gallery-desc').append('close')
                        $('' + arrow + 'READ MORE').insertBefore($('.rs-gallery-desc-wrapper'));
                        $('.hp-desc-btn').click(function(){
                        	if($('.rs-gallery-desc-wrapper').hasClass('open')){
                            	$('.rs-gallery-desc-wrapper').stop().hide('slide', {direction: 'right'}, 500).removeClass('open').attr('aria-hidden', 'true');
                                $(this).removeClass('open').stop().show('slide', {direction: 'left'}, 500);
                                $('.hp-desc-btn').attr('aria-label', 'Open Gallery Desciption');
                            } else {
                            	$('.rs-gallery-desc-wrapper').stop().show('slide', {direction: 'right'}, 500).addClass('open').attr('aria-hidden', 'false');
                                $(this).addClass('open').stop().hide('slide', {direction: 'left'}, 500);
                                if($('.rs-gallery-desc-wrapper a').size() > 0){
                                	$('.rs-galelry-desc-wrapper a').first().focus();
                                }
                                $('.hp-desc-btn').attr('aria-label', 'Close Gallery Desciption');
                            }
                        }).keydown(function(e){
                        	var c = e.which;
                            if((c == kEnter) || (c == kSpace)){
                            	e.preventDefault();
                            	if($('.rs-gallery-desc-wrapper').hasClass('open')){
                                    $('.rs-gallery-desc-wrapper').stop().hide('slide', {direction: 'right'}, 500).removeClass('open').attr('aria-hidden', 'true');
                                    $(this).removeClass('open').stop().show('slide', {direction: 'left'}, 500);
                                    $('.hp-desc-btn').attr('aria-label', 'Open Gallery Desciption');
                                } else {
                                    $('.rs-gallery-desc-wrapper').stop().show('slide', {direction: 'right'}, 500).addClass('open').attr('aria-hidden', 'false');
                                    $(this).addClass('open').stop().hide('slide', {direction: 'left'}, 500);
                                    $('.rs-gallery-desc a').first().focus();
                                    $('.hp-desc-btn').attr('aria-label', 'Close Gallery Desciption');
                                }
                            }
                        });
                        $('.rs-gallery-desc .close-btn').click(function(){
                        	if($('.rs-gallery-desc-wrapper').hasClass('open')){
                            	$('.rs-gallery-desc-wrapper').stop().hide('slide', {direction: 'right'}, 500).removeClass('open').attr('aria-hidden', 'true');
                                $('.hp-desc-btn').removeClass('open').stop().show('slide', {direction: 'left'}, 500);
                                $('.hp-desc-btn').attr('aria-label', 'Close Gallery Desciption');
                            } else {
                            	$('.rs-gallery-desc-wrapper').stop().show('slide', {direction: 'right'}, 500).addClass('open').attr('aria-hidden', 'false');
                                $('.hp-desc-btn').addClass('open').stop().hide('slide', {direction: 'left'}, 500);
                                $('.hp-desc-btn').attr('aria-label', 'Close Gallery Desciption');
                            }
                        }).keydown(function(e){
