        $('.gb-custom-search').html(search);
        $('#sw-mystart-search').remove();
        /*$('#sw-search-button').attr('role', 'button').keydown(function(e){ //commented out by Lyndsay - Case 00520462 - 5.8.17
        	var c = e.which;
            if(c == kSpace){
            	e.preventDefault();
            	SWGoToSearchResultsPageswsearchinput();
            }
        });*/
        $('#sw-search-button').attr('role', 'button'); //added by Lyndsay - Case 00520462 - 5.8.17
        $('#sw-search-button').on('keyup', function(e) { //added by Lyndsay - Case 00520462 - 5.8.17
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                $(this).click();
            }
        });$('#sw-search-input').on('keydown', function(e) { 
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13)) {
                $('#sw-search-button').click();
            }
        });


        if($('.hp-overlay-learnmore').data('target') == 'Self'){
        	$('.hp-overlay-learnmore').attr('target', '_self');
        } else {
        	$('.hp-overlay-learnmore').attr('target', '_blank');
        }

		//DOC fixes search box not allowing spaces

