			$("
" + myviewBtn + "").prependTo(".myaccount .sw-dropdown-list");
			$(".sw-mystart-button.pw").hide();
		}
        //$('#ui-btn-sitemanager').wrap('
');

        //-- Sroll Down and Top Buttons
        function scrollTo(target){
          $('html, body').animate({
            scrollTop: $(target).offset().top
          }, 1000);
          return false;
        };

        $('.down-btn').click(function(){
        	scrollTo('#hp-down-wrapper');
        });
        $('.top-btn').click(function(){
        	scrollTo('#gb-header-top');
        });
        $('.down-btn').keypress(function(e){
        	var c = e.which;
            if((c == kEnter) || (c == kSpace)){
            	e.preventDefault();
        		scrollTo('#hp-down-wrapper');
                $('#hp-content a').first().focus();
            }
        });
        $('.top-btn').keypress(function(e){
        	var c = e.which;
            if((c == kEnter) || (c == kSpace)){
            	e.preventDefault();
        		scrollTo('#gb-header-top');
        		$('#gb-header-top a').first().focus();
            }
        });
        //-- Custom Dropdown

        $('.gb-custom-dropdown').hover(function(){
        	var dropdown = $('.sw-dropdown', this);
            if(!(dropdown.hasClass('open'))){
              dropdown.slideDown().attr('aria-hidden', 'false').addClass('focus').addClass('open');
              $('.sw-dropdown li a', this).attr('tabindex', '-1');
            }
        },function(){//-- Mouse Leave
        	var dropdown = $('.sw-dropdown', this);
        	if(dropdown.hasClass('open')) {
            	dropdown.stop().slideUp().attr('aria-hidden', 'true').removeClass('focus').removeClass('open');
                $('.sw-dropdown li a', this).attr('tabindex', '-1');
            }
        }).focus(function(){ //-- On Focus 
        	var dropdown = $('.sw-dropdown', this);
            if(!(dropdown.hasClass('open'))){
              dropdown.stop().slideDown().attr('aria-hidden', 'false').addClass('focus').addClass('open');
              $('.sw-dropdown li a', this).attr('tabindex', '-1');
            }
        }).keydown(function(e){ //-- Go into Dropdown
          // Trigger the click event from the keyboard 
            var code = e.which;
        	var dropdown = $('.sw-dropdown', this);
            if (code == kDown) {
           		e.preventDefault();
                $('.sw-dropdown-list li:first-child a', this).addClass('first');
                if(!(dropdown.hasClass('inner'))){
                	dropdown.addClass('inner');
                    $('.sw-dropdown li:first-child a', this).focus();
                    $('.sw-dropdown li a', this).attr('tabindex', '0');
                }
            }
        }).blur(function(){ //-- Focus Leaves
        	var dropdown = $('.sw-dropdown', this);
            if(!(dropdown.hasClass('inner'))) {
            	dropdown.stop().slideUp().attr('aria-hidden', 'true').removeClass('focus').removeClass('open');
                $('.sw-dropdown li a', this).attr('tabindex', '-1');
            }

        });//-- End Keydown

        $('.gb-custom-dropdown a').keydown(function(e){
        	var c = e.which,
            	n = $(this).parent().next().children('a'),
            	p = $(this).parent().prev().children('a');
                if((c == kDown) || (c == kUp)){
                	if(c == kDown){
                    	n.focus();
                    } else {
                    	if($(this).hasClass('first')){
                        	$(this).parent().parent().parent().removeClass('inner').parent().focus();
                            $('.gb-custom-dropdown .sw-dropdown li a').attr('tabindex', '-1');
                        } else {
                    		p.focus();
                    	}
                    }
                }
        });//-- Inner Dropdown Docigation



		// ********** HEADER ********** //
		// ADD LOGO
		var logoSrc = jQuery.trim("/cms/lib/DE01903704/Centricity/Template/GlobalAssets/images///logos/logo.png");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
			$(".gb-logo").append('').removeClass("hidden");
		} else {
			$(".gb-logo").append('').removeClass("hidden");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		$(".sw-channel-item").attr("aria-haspopup","true");
		$(".sw-channel-dropdown li").attr("aria-haspopup","false");

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
