    function cssTriangle(){

    	var vpw = $(window).width();

        //-- Get Height of Container
        var height = $('#gb-header-bottom').height();

        //-- Divide by 2 so I can use for each side
        var tHeight = height / 2;

        //-- Build CSS size for Right arrow.
        var right = tHeight + 'px 0 ' + tHeight + 'px 10px';


        //-- Responsive CSS Triangles
        $('.header-arrow').css('border-width', right);


        $('#gb-global-icons-right-outer').css('top', (height + 84) + 'px');

    }

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
        $(".ui-article", container).each(function(){
        	var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});
        $('.hp.row.three .upcoming-column.left').attr('tabindex', '0').attr('role', 'button');
        $('.view-calendar-link').html('VIEW ALL' + arrow + '');

        //-- Open Date Event Detail
        $('.hp.row.three .upcoming-column.left').click(function(){
        	var right = $(this).siblings('.right'),
            	left = $(this),
                all = $('.upcoming-column.right');
            if(right.hasClass('open')){
            	right.removeClass('open').attr('aria-hidden', 'true');
                left.removeClass('open');
            } else {
            	$('.upcoming-column.left').removeClass('open');
                all.removeClass('open').attr('aria-hidden', 'true');
            	right.addClass('open').attr('aria-hidden', 'false');
                left.addClass('open');
            }
        });//-- End Click

        //-- Give and Assign ID's
        $('.hp.row.three .upcoming-column-container').each(function(k, v){
        	var id = 'event-' + k;
        	$('.upcoming-column.left', v).attr('aria-controls', id).attr('aria-expanded', 'false');
            $('.upcoming-column.right', v).attr('id', id);
            if(k == 0){
            	$('upcoming-column.left', v).attr('aria-expanded', 'true');
            }
        });

        //-- Key Board Funtionality
        $('.hp.row.three .upcoming-column.left').keydown(function(e){
        	var code = e.which,
            	right = $(this).siblings('.right'),
                left = $(this),
                first = $('.hp.row.three .upcoming-column.left.first'),
                next = $(this).parent('div').parent('li').next().children().children('.left'),
                prev = $(this).parent('div').parent('li').prev().children().children('.left'),
                last = $('.hp.row.three .upcoming-column.left.last'),
                current = $(this).parent('div').parent('li'),
                all = $('.upcoming-column.right');

            //-- Check What key was pressed
        	if(code == kEnter || code == kDown || code == kSpace || code == kUp){
            	e.preventDefault();
                if(right.hasClass('open')){
                	if(code == kDown){
                    	right.children().children().children('a').first().focus();
                    } else {
                       	$('.upcoming-column.left').attr('aria-expanded', 'false');
                        right.removeClass('open').attr('aria-hidden', 'true');
                        left.removeClass('open').attr('aria-expanded', 'false');
                    }
                } else {
                    all.removeClass('open').attr('aria-hidden', 'true');
                    right.addClass('open').attr('aria-hidden', 'false');
                    $('.upcoming-column.left').removeClass('open').attr('aria-expanded', 'false');
                    left.addClass('open').attr('aria-expanded', 'true');
                    right.children().children().children('a').first().focus();
                }
            } else if(code == kLeft || code == kRight){
            	e.preventDefault();
                if(code == kRight){
                	if(left.hasClass('last')){
                		first.focus();
                    } else {
                    	next.focus();
                    }
                } else {
                	if(left.hasClass('first')){
                    	last.focus();
                    } else {
                    	prev.focus();
                    }
                }
            }

        });//-- End Keydown

        $('.hp.row.three .upcoming-column.right a').keydown(function(e){
        	var code = e.which,
            	link = $(this),
                left = $(this).parent().parent().parent().siblings('.left'),
                next = $(this).parent().parent().next().children().children('a'),
                prev = $(this).parent().parent().prev().children().children('a');
            if(code == kUp || code == kDown){
            	e.preventDefault();
				if(code == kUp) {
                	if(link.hasClass('first')){
                    	left.focus();
                    } else {
            			prev.focus();
                	}
                } else if(code == kDown) {
            		next.focus();
                }
            }
        });//-- End KeyDown
	}

	function searchText() {
		//DOC custom mystart search placeholder
        //SEARCH TEXT
        $(".gb-custom-search input#sw-search-input").removeAttr("placeholder");
		$(".gb-custom-search input#sw-search-input").attr("value", "SEARCH...");
		$(".gb-custom-search input#sw-search-input").focus(function() {
			if($(this).val() == "SEARCH...") {
				$(this).val("");
			}
		});
		$(".gb-custom-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("SEARCH...");
			}
		});
	}

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};

	//usage
	//console.log(hexToRgb("#FFFFFF"));
	//usage with RGBA
	//console.log("rgba("+hexToRgb('#FFFFFF')+",0.5)");

