  (function() {
    window.NYTG = window.NYTG || {};
    window.NYTG.ASSETS = "https://static01.nyt.com/newsgraphics/2020/03/16/coronavirus-maps/eb6506eb0a6979fb12e76a8a29b7e2af4ea14f93/".slice(0, -1);
    window.NYTG.BIG_ASSETS = "https://static01.nyt.com/newsgraphics/2020/03/16/coronavirus-maps/assets/".slice(0, -1);

    var supports_import = false;
    var can_use_import = location.hostname.indexOf('preview.n' + 'yt.net') == -1;
    var shimport_url = window.NYTG.ASSETS + "/shimport@1.0.1.js";
    var shimport_promise;

    try {
      new Function("if(0)import('')")();
      supports_import = true;
    } catch(e) { }

    if (!can_use_import || !supports_import) {
      shimport_promise = new Promise(function(fulfil, reject) {
        var s = document.createElement("script");
        s.src = shimport_url;
        s.onload = function() {
          fulfil(__shimport__);
        };
        s.onerror = reject;

        document.head.appendChild(s);
      });
    }

    window.NYTG.load = function(module) {
      var url = new URL(module, location.href).href;

      if (can_use_import && supports_import) {
        return eval("import('" + url + "')");
      } else {
        return shimport_promise.then(function(shimport) {
          return shimport.load(url);
        });
      }
    };

    // for --live
    window.NYTG.subscribers = {};
    window.NYTG.watch = function(url, cb) {
      if (!window.NYTG.subscribers[url]) window.NYTG.subscribers[url] = [];
      window.NYTG.subscribers[url].push(cb);
      window.NYTG.load(url).then(cb);
    };
  }());

  (function() {
    // Check for template type: standard, minimal, or blank
    var templateType   = "template-" + ("" || "standard");
    // Check for icon style options
    var templateStyle  = "template-option-" + ("" || "black-icons");
    // Check for dark mode flag
    var isDarkMode     = "".match(/y|yes|true/) !== null;
    var darkMode       = (isDarkMode) ? " " + "template-mode-dark" : "";
    // Check for feature style flag
    var isFeatureStyle = "".toLowerCase().match(/feature|featured|feat/) !== null;
    var featureStyle   = (isFeatureStyle) ? "template-style-feature" : "template-style-standard";
    // Set classes on body element
    var tBody          = document.body.classList.add(...[templateType.trim(), templateStyle.trim(),
      darkMode.trim(), featureStyle.trim()].filter(d => !!d));
  })();



























































