var runs = 1, done = false;
var intervalId = setInterval(function() {
  if (dataLayer && performance.getEntriesByName('int:results-map:finish').length) {
    dataLayer.push({
      event: 'performance',
      pageview: {
        performance: {
          resultsMapStart: Math.floor(performance.getEntriesByName('int:results-map:start')[0].startTime),
          resultsMapFinish: Math.floor(performance.getEntriesByName('int:results-map:finish')[0].startTime),
        }
      }
    });
    done = true;
  }
  if (runs++ === 30) done = true;
  if (done) clearInterval(intervalId);
}, 1500);

