body.script:
  (function () {
    function isBridgeCommandAvailable(command, supportedBridgeCommands) {
  // The shape of root.navigator.native.bridgeCommands looks like this:
  // {
  //  "ping": {
  //      "enabled": true
  //   },
  //   "resize": {
  //      "enabled": true
  //    }
  // }
  try {
    var commandAvailable = supportedBridgeCommands[command];

    if (!!commandAvailable === false) {
      return false;
    }

    return !!commandAvailable.enabled;
  } catch (e) {
    // if something goes wrong, swallow error and assume unsupported
    return false;
  }
}
    window.NativeBridge = (function initNativeBridge(root) {
  var _root$navigator, _root$navigator$nativ;

  var checkAvailable = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : true;
  var featureDetectionDictionary = arguments.length > 2 ? arguments[2] : undefined;
  var NativeBridge = root && root.NativeBridge || {}; // default feature list

  var supportedBridgeCommands = featureDetectionDictionary || (root === null || root === void 0 ? void 0 : (_root$navigator = root.navigator) === null || _root$navigator === void 0 ? void 0 : (_root$navigator$nativ = _root$navigator["native"]) === null || _root$navigator$nativ === void 0 ? void 0 : _root$navigator$nativ.bridgeCommands);

  if (typeof root !== 'undefined') {
    root.NYTG = root.NYTG || {};
    root.NYTG.currentAsyncID = 0;
    root.NYTG.storedCallbacks = {}; // Private
    // Do not call this command, for internal use only.

    var dispatchNativeBridgeCommandAsync = function dispatchNativeBridgeCommandAsync(type, options) {
      var asyncID = root.NYTG.currentAsyncID;
      root.NYTG.currentAsyncID += 1;
      var promise = new Promise(function (fulfill, reject) {
        if (checkAvailable && !NativeBridge.isBridgeCommandAvailable(type, supportedBridgeCommands)) {
          throw new Error("[NativeBridge] command ".concat(type, " not supported"));
        }

        try {
          root.NYTG.storedCallbacks[asyncID] = function (result) {
            if (!result.success) {
              var error = new Error(result.error);
              error.details = result.error.details;
              return reject(error);
            }

            return fulfill(result);
          };
        } catch (error) {
          var newError = new Error("Something went wrong creating the callback with ID ".concat(asyncID, ". Error: ").concat(JSON.stringify(error)));
          reject(newError);
        }
      });

      try {
        root.NYTG.enqueue(JSON.stringify({
          id: asyncID,
          type: type,
          options: options,
          senderURI: root.asset.uri
        }));
      } catch (error) {
        // swallow enqueing error
        console.log("[NativeBridge]: Caught error while enqueing: ".concat(error));
      }

      return promise;
    };

    var checkIsInVariantAsync = function checkIsInVariantAsync(variant) {
      return new Promise(function (fulfill, reject) {
        try {
          var _root$navigator2, _root$navigator2$nati;

          var variantsAvailable = root === null || root === void 0 ? void 0 : (_root$navigator2 = root.navigator) === null || _root$navigator2 === void 0 ? void 0 : (_root$navigator2$nati = _root$navigator2["native"]) === null || _root$navigator2$nati === void 0 ? void 0 : _root$navigator2$nati.activeABTestVariants;

          if (variantsAvailable === undefined) {
            return reject(new Error('root.navigator.native.activeABTestVariants is undefined'));
          }

          if (Array.isArray(variantsAvailable)) {
            return fulfill(variantsAvailable.indexOf(variant) > -1);
          }

          return reject(new Error('Variant Array is not an array'));
        } catch (error) {
          var newError = new Error("Something went wrong checking if user is in variant. Error: ".concat(JSON.stringify(error)));
          return reject(newError);
        }
      });
    }; // Do not call this command, for internal use only.
    // Note — if NYTG.onCommandResult is already set, don't overwrite it
    // This is to preserve backwards compatibility w/ interactives that use the AR interface


    if (!root.NYTG.onCommandResult) {
      root.NYTG.onCommandResult = function (result) {
        var callback = root.NYTG.storedCallbacks[result.id];

        if (!callback) {
          console.log("Callback with ID ".concat(result.id, " not found."));
          return;
        }

        callback(result);
        delete root.NYTG.storedCallbacks[result.id];
      };
    } // Public
    // Hybrid Config


    NativeBridge.isBridgeCommandAvailable = function (type) {
      return isBridgeCommandAvailable(type, supportedBridgeCommands);
    };

    NativeBridge.hybridConfig = function () {
      return new Promise(function (fulfill, reject) {
        document.addEventListener('DOMContentLoaded', function () {
          if (typeof root.config !== 'undefined') {
            fulfill(root.config);
          } else {
            var error = new Error('No root config found.');
            reject(error);
          }
        });
      });
    }; // Interactive Commands
    // Note: Ping is defined for both Articles and Interactives


    NativeBridge.ping = function () {
      return dispatchNativeBridgeCommandAsync('ping');
    };

    NativeBridge.resize = function (height) {
      return dispatchNativeBridgeCommandAsync('resize', {
        height: height
      });
    };

    NativeBridge.getAutoPlay = function () {
      return dispatchNativeBridgeCommandAsync('getAutoPlay');
    };

    NativeBridge.setPullToRefreshEnabled = function (value) {
      return dispatchNativeBridgeCommandAsync('setPullToRefreshEnabled', {
        value: value
      });
    }; // Article Commands


    NativeBridge.sendAnalytic = function (eventName, options) {
      return dispatchNativeBridgeCommandAsync('sendAnalytic', {
        eventName: eventName,
        options: options || {}
      });
    };

    NativeBridge.followChannel = function (uri) {
      return dispatchNativeBridgeCommandAsync('followChannel', {
        uri: uri
      });
    };

    NativeBridge.unfollowChannel = function (uri) {
      return dispatchNativeBridgeCommandAsync('unfollowChannel', {
        uri: uri
      });
    };

    NativeBridge.isInVariant = function (variant) {
      return checkIsInVariantAsync(variant);
    };
  }

  return NativeBridge;
})(window, false)
  })()
