		head.ready("rsMainMenu", function(){
			var additionalHeaders = "", additionalLinks = "", menuLinksOne = "", menuLinksTwo = "", menuLinksThree = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["e-Notification", "https://d86.hinsdale86.org/Page/473", "_blank"],
				["Tip Line", "https://d86.hinsdale86.org/Page/679", "_blank"],
				["Employment", "https://www.applitrack.com/d86/onlineapp/default.aspx", "_blank"],
				["Staff Directory", "http://hinsdale86.schoolwires.net/domain/166", "_blank"],
				["Icon 5", "#", "_blank"]
			];
			var menuIconNum = ("4" == "5;4;3;2;1;0") ? 0 : "4";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", " + menuGlobalIcons[i][2] + ";";
			}

			if(menuIconNum > 0) {
				additionalHeaders = "Popular Links";
			}

			var menuFooterLinksOne = [
				["", "","_blank"],
		                ["", "#","_blank"],
		                ["", "#","_blank"]
			];

			var menuFooterLinksTwo = [
				["", "#","_blank"],
                		["", "#","_blank"],
                		["", "#","_blank"]
			];

			var menuFooterLinksThree = [
				["", "#","_blank"],
                		["", "#","_blank"],
                		["", "#","_blank"]
			];


			for(var i = 0; i < 3; i++) {
		                if(menuFooterLinksOne[i][0] != "") {
						    menuLinksOne += "2, " + menuFooterLinksOne[i][0] + ", " + menuFooterLinksOne[i][1] + ", " + menuFooterLinksOne[i][2] + ";";
		                }    
			}
			for(var j = 0; j < 3; j++) {
				if(menuFooterLinksTwo[j][0] != "") {
						    menuLinksTwo += "3, " + menuFooterLinksTwo[j][0] + ", " + menuFooterLinksTwo[j][1] + ", " + menuFooterLinksTwo[j][2] + ";";
				}    
			}
			for(var k = 0; k < 3; k++) {
				if(menuFooterLinksThree[k][0] != "") {
						    menuLinksThree += "4, " + menuFooterLinksThree[k][0] + ", " + menuFooterLinksThree[k][1] + ", " + menuFooterLinksThree[k][2] + ";";
				}    
			}

			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#111111", // DEFAULT - #2C363E
			        "secondaryColor"        : "#292A2B", // DEFAULT - #222A30
			        "accentColor"           : "#BABABA", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#rs-channel-inner-menu",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "Menu",
			        "showSchools"           : "no",
			        "showTranslate"         : "no",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : additionalHeaders + ";;;", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : additionalLinks + menuLinksOne + menuLinksTwo + menuLinksThree, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "4"
			});
        });

