		head.ready("sameHeight", function() {
			$("body").csRsSameHeights({
			        resizeElements : [
			                {
			                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
			                        "parent"                : "#hp-content",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
			                        "columns"               : ".hp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
			                        "gutter"                : 0,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
			                        "applySameHeights"      : ["960","768","640"]        // OPTIONS - ["960", "768", "640", "480", "320"]
			                },
			                {
			                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
			                        "parent"                : "#sp-content",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
			                        "columns"               : ".sp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
			                        "gutter"                : 0,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
			                        "applySameHeights"      : ["960"]        // OPTIONS - ["960", "768", "640", "480", "320"]
			                }
			        ]
			});
		});

		$("body").viewFullSite({
			"breakPoint"		: 1023, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"buttonContainer"	: "body",
			"useDefaultCSS"		: "yes",
			"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
		});

	});

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				tabbedApps();
			break;
			case "768":
				tabbedApps();
				if(!$(".hp.column.one #sw-content-container1").length){
					$("#sw-content-container1").clone().appendTo(".hp.column.one");
				};	
			break;
			case "640":
				tabbedApps();
				if(!$(".hp.column.one #sw-content-container1").length){
					$("#sw-content-container1").clone().appendTo(".hp.column.one");
				};	
			break;
			case "480":

				if(!$(".hp.column.one #sw-content-container1").length){
					$("#sw-content-container1").clone().appendTo(".hp.column.one");
				};
				if(!$(".hp.column.two #sw-content-container8").length){
					$("#sw-content-container8").clone().appendTo(".hp.column.two");
					$(".hp.column.two #sw-content-container8 .ui-widget.app").first().addClass("first");
				};
			break;
			case "320":

				if(!$(".hp.column.one #sw-content-container1").length){
					$("#sw-content-container1").clone().appendTo(".hp.column.one");
				};
				if(!$(".hp.column.two #sw-content-container8").length){	
					$("#sw-content-container8").clone().appendTo(".hp.column.two");
					$(".hp.column.two #sw-content-container8 .ui-widget.app").first().addClass("first");
				};
			break;
		}
	}

	function mediaRotator() {
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
				// PHOTO GALLERY ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
					"imageWidth" 			: 1000,
			        	"imageHeight" 			: 375,
			        	"appHeader" 			: "no",
			        	"titles" 			: "yes",
			        	"captions" 			: "yes",
			        	"playPause" 			: "no",
			        	"mainControls" 			: "yes",
					"galleryOverlay" 		: "no",
					"linkedElement" 		: ["all"], // ["image", "title", "overlay", "all"]
			        	"contMoreLink" 			: "no",
			        	"contMoreLinkText" 		: "Read More",
			        	"descMoreLink" 			: "no",
			        	"descMoreLinkText" 		: "Read More",
			        	"bullets" 			: "yes",
			        	"bulletControls" 		: "no",
			        	"thumbnails" 			: "no",
			        	"thumbnailWidth" 		: 122,
			        	"thumbnailHeight" 		: 53,
			        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
			        	"hoverPause" 			: "yes",
					"autoRotate" 			: "yes",
			        	"transitionType" 		: "slide", // fade or slide
			        	"transitionSpeed" 		: 2,
			        	"onImageLoad" 			: function() { },
			        	"allImagesLoaded" 		: function() { },
			        	"onThumbnailLoad" 		: function() { },
			        	"allThumbnailsLoaded" 		: function() { },
			        	"onTransitionStart" 		: function() { },
			        	"onTransitionEnd" 		: function() { },
			        	"allLoaded" 			: function() {

						var taglineOuterWidth = $("#hp-slideshow .rs-photo-gallery-container #hp-tagline-outer").width();
						var taglineImageWidth = $("#hp-slideshow .rs-photo-gallery-container #hp-tagline-outer #hp-tagline-image").width();
						var taglineTextWidth = $("#hp-slideshow .rs-photo-gallery-container #hp-tagline-outer #hp-tagline").width();
						var taglineOffset = (taglineOuterWidth - 63 - taglineTextWidth)/2;
						$("#hp-slideshow .rs-photo-gallery-container #hp-tagline-outer").css({
											"padding-left": taglineOffset,
											"padding-right": taglineOffset
						});
						$("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-control.back").prependTo("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-bullets");
			        	 	$("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-control.next").appendTo("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-bullets");
			        	 	$("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-bullets-outer").next("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-desc").andSelf().wrapAll('
');
			        	 	$("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-desc").children().wrapAll('
');
			        	 	var tagline = '



A Tradition of
EXCELLENCE';
						$("#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-desc-outer").append(tagline);
						$("#rs-slideshow-bottom").append(tagline);
			        	 }
				});
			}
		});
	}

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});


		//ACCORDIAN HEADER CLICK
		$("#hp-content div.ui-widget-header").click(function(){
			if($(this).parent().hasClass("active")) {
				$(this).parent().removeClass("active");
			} else {
				var clickedApp = $(this).parent().parent().attr("id");

				$("div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}	

	function tabbedApps(){	
		head.ready("tabBlocks", function() {
			//TABBED APPS
	        	if(!$(".tabs-container").length){
		        	$("div.hp.column.two div#hp-tabs div#sw-content-container2").tabBlocks();
		        	$(".tab-button:eq(0)").addClass("first");
		        	$(".tab-button").addClass("border-box");
			}
		});	
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
COVID-19 Updates from D86");
		}
        }

