			$("#gb-search").toggleClass("active");*/

             searchHoverTimer = "";
                    if($("#gb-search-bottom").hasClass("open")){
                    $(this).attr("aria-hidden","false").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-bottom").removeClass("open").slideUp("3000").attr("aria-hidden","true");
                            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

                    } else {
                    	$(this).attr("aria-hidden","true");
                        $("#gb-search-bottom").addClass("open").slideDown("3000").attr("aria-hidden","false");
