		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/IL01904717/Centricity/Template/GlobalAssets/images///customLogos/hinsdale_District_blue.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
