		if(siteNameLength > 4){
			siteNameEnd = jQuery.trim(siteName.splice(-4, siteName.length).toString().replace(/,/g, " "));
		} else {
			siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
