        $('#back-to-top').on('click keypress', function(e){
          if(a11yClick(e) === true){
           	e.preventDefault();
        		$("html, body").animate({ scrollTop: $("body").offset().top}, 500);
                setTimeout(function(){ $("li.sw-channel-item:first-child a").focus(); }, 3000);
          }
