                        $(".ui-widget-header",this).attr({
                            "role": "button",
                            "aria-expanded": "false",
                            "aria-haspopup": "true",
                            "tabindex": "0"
                        });

                        $(".ui-widget-app-detail",this).attr({
                            "aria-hidden": "true"
                        });

	        		$(document).on("click touchstart", "body", function() {
	        			if($(".ui-widget-app-detail.active").length) {
	        				var activeParent = $(".ui-widget-app-detail.active").parent();
	        				$(".ui-widget-app-detail", activeParent).removeClass("active").animate({
											opacity: 0,
										    	right: "0"
										  }, 500, function() {

										  }).fadeOut("500");
						activeParent.removeClass("active");
						$("#hp-column-two-overlay").css({
							"opacity": "0",
							"z-index": "0"
						});
						$(".icon-font-arrow", activeParent).not(".icon-font.twitter", activeParent).css({
							"-webkit-transform": "rotate(-90deg)",
							"-moz-transform": "rotate(-90deg)",
							"-o-transform": "rotate(-90deg)",
							"-ms-transform": "rotat(-90deg)",
							"transform": "rotate(-90deg)"
						});
	        			}
	        		});

