     function a11yClick(event){
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if((code === 32)|| (code === 13)){
                return true;
            }
        }
        else{
            return false;
        }
    }

    function accessibility() {

        /*$(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(this).attr("role", "button");
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
        });*/

        $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a").attr("tabindex", "0");

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    //$(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                          $(this).addClass("open").attr("aria-expanded","true");
                          $(".selector", this).attr("aria-hidden","false");
                          $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden","false");
                          $(".sw-dropdown-list li:first-child a", this).focus();
                      } else {
                      		$(this).removeClass("open").attr("aria-expanded","false");;
                            $(".selector", this).attr("aria-hidden","true");
                            $(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden","true");
                            $(this).focus();
                      }
                      $(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM

                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
                case csKeyCodes.tab:
                    e.preventDefault();

                    $(this).parent().parent().parent().slideUp("slow", "swing").attr("aria-hidden","true");
                    $(this).parent().parent().parent().parent().removeClass("open").focus();
                break;
            }
        });

        $("#sw-myaccount").attr("aria-expanded","false");
        $("#sw-myaccount").attr("aria-haspopup","true");
        $("#sw-myaccount-list").attr("aria-hidden","true")
        $("#sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        }); 

        $("#sw-myaccount").attr("role","button");

        $("#sw-myaccount").click(function(e) {
        	if(!$(this).hasClass("open")) {
                  // TOGGLE THE DROPDOWN
                $(this).addClass("open").attr("aria-expanded","true");
                $("#gb-mystart #sw-myaccount-list").attr("aria-hidden","false");
                $("#gb-mystart #sw-myaccount-list").find("li:first-child a").focus();
            } else {
                $("#gb-mystart #sw-myaccount-list").attr("aria-hidden","true");
                $(this).removeClass("open").attr("aria-expanded","false").focus();
            }
        });

        $("#sw-myaccount").hover(function(e) {
                $("#gb-mystart #sw-myaccount-list").attr("aria-hidden","true");
                $(this).removeClass("open").attr("aria-expanded","false").focus();

        });

        $("#sw-myaccount").keydown(function(e) {

            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    //$(this).next("#sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                 	if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                     	$(this).addClass("open").attr("aria-expanded","true");
                        $("#gb-mystart #sw-myaccount-list").slideDown("slow", "swing").attr("aria-hidden","false");
                      	$("#gb-mystart #sw-myaccount-list").find("li:first-child a").focus();
                    } else {
                    	$("#gb-mystart #sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden","true");
                      	$(this).removeClass("open").attr("aria-expanded","false").focus();
                    }
                    // FOCUS FIRST ITEM

                break;
            }
        });    

        $("#sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                break;

                case csKeyCodes.tab:
                    e.preventDefault();

                    $("#gb-mystart #sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden","true");
                    $("#gb-mystart #sw-myaccount").removeClass("open").attr("aria-expanded","false").focus();
                break;
            }
        });

        $("#gb-search").keydown(function(e) {

            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();

					/*$("#gb-search-bottom").slideToggle("3000").attr("aria-hidden", ($("#gb-search-bottom").attr("aria-hidden") == "true") ? "false" : "true");
            		$("#gb-search").attr("aria-expanded", ($("#gb-search").attr("aria-expanded") == "true") ? "false" : "true")
					$("#gb-search").toggleClass("active");*/
                    searchHoverTimer = "";
                    if($("#gb-search-bottom").hasClass("open")){
                    $(this).attr("aria-hidden","false").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-bottom").removeClass("open").slideUp("3000").attr("aria-hidden","true");
                            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

                    } else {
                    	$(this).attr("aria-hidden","true");
                        $("#gb-search-bottom").addClass("open").slideDown("3000").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$("#gb-search-bottom #sw-search-input").focus().attr("tabindex","0");

                        }, 1000);

                    }


                break;
            }
		});

        $("#sw-search-button").keydown(function(e) {

            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.tab:
                    e.preventDefault();

                    searchHoverTimer = "";

                    $("#gb-search").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-bottom").removeClass("open").slideUp("3000").attr("aria-hidden","true");
                        $("#gb-search-container #sw-search-input").attr("tabindex","-1");




                break;
            }
		});

    }    

	function overlay() {
		//SET THE LEFT AND RIGHT OVERLAYS
		var getWidth = $(window).width();
		var slideshowWidth = $(".rs-photo-gallery-container").width();
		var sideOffset = (getWidth - slideshowWidth)/2;
		var mainOffset = slideshowWidth + sideOffset;

		$("#hp-slideshow-bg-left").css("right",mainOffset);
		$("#hp-slideshow-bg-right").css("left",mainOffset);
	}

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

		$('.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles .upcoming-column.right .ui-article-description').each(function(){
			$(".sw-calendar-block-time", this).before($(".sw-calendar-block-title",this));
		});
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function buildStyleSheet(){
		var captionBGColor93 = "rgba(" + hexToRgb('#0E1F38') + ", .93)";
		var contentBGColor75 = "rgba(" + hexToRgb('#0E1F38') + ", .75)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow .rs-photo-gallery-container .rs-photo-gallery-desc {' +
					'	background: ' + captionBGColor93 + ';' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

