            $('li.sw-channel-item.home-channel > a').attr('aria-label', 'Home').html('');
        }

        if($('li.sw-channel-item:last > a > span').html().toLowerCase() == 'calendar'){
        	$('li.sw-channel-item:last').addClass('calendar-channel');
            $('li.sw-channel-item.calendar-channel > a').attr('aria-label', 'Calendar').html('');
        }

        $('li.sw-channel-item.home-channel').appendTo('#channel-navigation');
        $('li.sw-channel-item.calendar-channel').appendTo('#channel-navigation');
        $('#channel-navigation').append('
');
        $('#gb-search-trigger').appendTo('#gb-search-trigger-parent');

		pgScreenSize();
		useChannelForSection();
		modEvents();
        dynamicContent();
        fitTextSetup();
		customDropdowns('point-up', 'point-down');
        clearLogo();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
        var initW = 1220, initH = 610; /* used to calculate headline card container sizing */
        var cont = $('#hp-headline-cards .ui-widget-detail');

        if( getWidth < 1024 ){ //768 and down
        	initW = 620;
            initH = 1219;

            $('#hp-headline-cards .app.headlines .ui-articles > li .card-info').off('hover');
            if( getWidth < 640 ){ //480 and down
            	initW = 305;
                initH = 1070;

                if($('.hp').length > 0){
                	if($('#gb-sticky-header-outer > .max-width > #gb-header-outer').length <= 0){
                    	$('#gb-sticky-header-outer > .max-width').append($('#rs-menu-btn'));
                    }

                    if($('#gb-sticky-header-outer > .max-width > #gb-search-trigger').length <= 0){
                    	$('#gb-sticky-header-outer > .max-width').append($('#gb-search-trigger'));
                    }
                }else{
                	if( $('#gb-channel-list > #rs-menu-btn').length <= 0 ){
                		$('#gb-channel-list').prepend($('#rs-menu-btn'));
                    }

                    if($('#gb-channel-list > #gb-search-trigger').length <= 0){
                    	$('#gb-channel-list').append($('#gb-search-trigger'));
                    }
                }
            }else{ //768 to 640
            	if($('#gb-channel-list > #rs-menu-btn').length <= 0){
                	$('#gb-channel-list').prepend($('#rs-menu-btn'));
                }
                if($('#gb-channel-list > #gb-search-trigger').length <= 0){
                	$('#gb-channel-list').append($('#gb-search-trigger'));
                }
            }

            if( $('.gb.footer.two > .cs-top').length > 0 ){
            	$('#gb-footer').append($('.gb.footer.two > .cs-top'));
            }
        }else{
        	if( $('.gb.footer.two > .cs-top').length <= 0 ){
            	$('.gb.footer.two').prepend($('#gb-footer > .cs-top'));
            }
            if($('#gb-channel-list > #rs-menu-btn').length <= 0){
                $('#gb-channel-list').prepend($('#rs-menu-btn'));
            }
            if($('#gb-search-trigger-parent > #gb-search-trigger').length <= 0){
                $('#gb-search-trigger-parent').append($('#gb-search-trigger'));
            }
        }

		var contW = cont.width();
        var ratio = initH / initW;
        var contHeight = contW * ratio;
        cont.css('height', contHeight);

	}

